import React from 'react'
import Cookies from 'universal-cookie';
import { Redirect } from 'react-router-dom'
// import CheckBox from '../subComponent/CheckBox';
import Grid from '@material-ui/core/Grid'
import Loader from 'react-loader-spinner'
import { createStructuredSelector } from 'reselect';
import { connect } from 'react-redux';
import { withRouter } from 'react-router';
import { withStyles } from '@material-ui/styles';
import { compose } from 'redux';
import { getPermintaanPinjamanDetailFunction } from '../permintaanPinjaman/saga';

const styles = (theme) => ({
    container: {
      flexGrow: 1,
    },
  });

const cookie = new Cookies();

class rolePermissionDetail extends React.Component{
    _isMounted = false;
    
    state = {
      diKlik:false,
      errorMessage:'',
      listRolePermission: [],
      listRole: {},
      role : {},
      roleId: 0,
      disabled: true,
      loading: true,
    };

    componentDidMount(){
      this._isMounted = true;
      this.setState({
        roleId: this.props.match.params.id,
      },() => {
        this.refresh();
      })
      
    }

    componentWillUnmount() {
      this._isMounted = false;
    }

    refresh = async function(){
      const param = {};

      const data = await getPermintaanPinjamanDetailFunction(param);

      if(data) {
          if(!data.error) {
            this.setState({
              listRole: data.dataRole,
              listRolePermission: data.dataRolePermission,
              loading: false,
            })
          } else {
            this.setState({
              errorMessage: data.error,
              disabled: true,
              loading: false,
            })
          }      
      }
    }

    btnCancel = ()=>{
      this.setState({diKlik:true})
    }
    componentWillReceiveProps(newProps){
      this.setState({errorMessage:newProps.error})
    }

    checkingRole = (role, idRolePermission) => {
        for (const key in role) {
          if (
            role[key].id.toString().trim() ===
            idRolePermission.toString().trim()
          ) {
            return true;
          }
        }
        return false;
    }

    render(){
        if(this.state.diKlik){
            return <Redirect to='/listRolePermission'/>            
        } else if (this.state.loading){
          return  (
            <div  key="zz">
              <div align="center" colSpan={6}>
                <Loader 
                  type="Circles"
                  color="#00BFFF"
                  height="40"	
                  width="40"
                />   
              </div>
            </div>
          )
        } else if(cookie.get('token')){
            return(
                <div className="container mt-4">
                  <h3>Role Permission - Detail</h3>
                  
                  <hr/>

                  <Grid container spacing={0}>
                    <Grid item sm={6} xs={6}>
                      <Grid container spacing={0}>
                        <Grid item sm={6} xs={12}>
                          ID Pinjaman
                        </Grid>
                        <Grid item sm={6} xs={12}>
                          : {this.state.dataDetail && this.state.dataDetail.id}
                        </Grid>
                      </Grid>
                    </Grid>
                    <Grid item sm={6} xs={6}>
                      <Grid container spacing={0}>
                        <Grid item sm={6} xs={12}>
                          Rekening Pinjaman
                        </Grid>
                        <Grid item sm={6} xs={12}>
                          : {this.state.dataDetail && this.state.dataDetail.rekening}
                        </Grid>
                      </Grid>
                    </Grid>
                    <Grid item sm={6} xs={6}>
                      <Grid container spacing={0}>
                        <Grid item sm={6} xs={12}>
                          Nama Nasabah
                        </Grid>
                        <Grid item sm={6} xs={12}>
                          : {this.state.dataDetail && this.state.dataDetail.nama}
                        </Grid>
                      </Grid>
                    </Grid>
                    <Grid item sm={6} xs={6}>
                      <Grid container spacing={0}>
                        <Grid item sm={6} xs={12}>
                          Status Pinjaman
                        </Grid>
                        <Grid item sm={6} xs={12}>
                          : {this.state.dataDetail && this.state.dataDetail.status}
                        </Grid>
                      </Grid>
                    </Grid>
                  </Grid>

                  <Grid container spacing={0}>
                    <Grid item sm={6} xs={12}>
                      <Grid container spacing={0}>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={12}>
                              Pinjaman Pokok
                            </Grid>
                            <Grid item sm={6} xs={12}>
                              : Rp {this.state.dataDetail && this.state.dataDetail.pinjaman_pokok}
                            </Grid>
                          </Grid>
                        </Grid>
                      
                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={12}>
                              Tenor (Bulan)
                            </Grid>
                            <Grid item sm={6} xs={12}>
                              : {this.state.dataDetail && this.state.dataDetail.tenor}
                            </Grid>
                          </Grid>
                        </Grid>

                      </Grid>
                    </Grid>

                    <Grid item sm={6} xs={12}>
                      <Grid container spacing={0}>

                      </Grid>
                    </Grid>

                    
                    <Grid item sm={6} xs={6}>
                      <Grid container spacing={0}>
                        <Grid item sm={6} xs={6}>
                          Tujuan Pinjaman
                        </Grid>
                        <Grid item sm={6} xs={6}>
                          : {this.state.dataDetail && this.state.dataDetail.tujuan_pinjaman}
                        </Grid>
                      </Grid>
                    </Grid>
                    
                    <Grid item sm={6} xs={6}>
                      <Grid container spacing={0}>
                        <Grid item sm={6} xs={12}>
                          Detail Tujuan
                        </Grid>
                        <Grid item sm={6} xs={12}>
                          : {this.state.dataDetail && this.state.dataDetail.detail}
                        </Grid>
                      </Grid>
                    </Grid>
                    <Grid item sm={6} xs={6}>
                      <Grid container spacing={0}>
                        <Grid item sm={6} xs={12}>
                          Total Pinjaman
                        </Grid>
                        <Grid item sm={6} xs={12}>
                          : Rp {this.state.dataDetail && this.state.dataDetail.total}
                        </Grid>
                      </Grid>
                    </Grid>
                    <Grid item sm={6} xs={6}>
                      <Grid container spacing={0}>
                        <Grid item sm={6} xs={12}>
                          Tanggal Pengajuan
                        </Grid>
                        <Grid item sm={6} xs={12}>
                          : {this.state.dataDetail && this.state.dataDetail.pengajuan_date}
                        </Grid>
                      </Grid>
                    </Grid>
                    <Grid item sm={6} xs={6}>
                      <Grid container spacing={0}>
                        <Grid item sm={6} xs={12}>
                          Angsuran Perbulan
                        </Grid>
                        <Grid item sm={6} xs={12}>
                          : Rp {this.state.dataDetail && this.state.dataDetail.angsuran}
                        </Grid>
                      </Grid>
                    </Grid>
                    <Grid item sm={6} xs={6}>
                      <Grid container spacing={0}>
                        <Grid item sm={6} xs={12}>
                          Tanggal Persetujuan
                        </Grid>
                        <Grid item sm={6} xs={12}>
                          : {this.state.dataDetail && this.state.dataDetail.persetujuan_date}
                        </Grid>
                      </Grid>
                    </Grid>
                  </Grid>
                 

                </div>
            )
        } else if(!cookie.get('token')){
          return (
              <Redirect to='/login' />
          )    
        }
       
    }
}

export function mapDispatchToProps(dispatch) {
    return {
    //   getSourceTransaction: () => {
    //     dispatch(sourceTransactionRequest());
    //   },
    //   handleRedirect: (route) => {
    //     dispatch(push(route));
    //   },
    };
}
  
export const mapStateToProps = createStructuredSelector({
  // user: getUserState(),
  // menu: getMenu(),
  // fetching: getFetchStatus(),
});

const withConnect = connect(
    mapStateToProps,
    mapDispatchToProps
);

const withStyle = withStyles(styles);

export default compose(
    withConnect,
    withStyle,
    withRouter
  )(rolePermissionDetail);
