export function padLeft(dataInt, pjg) {
  let str = dataInt;
  for (let w = 0; w < pjg - dataInt.toString().length; w++) {
    str = "0" + str;
  }
  return str;
}

export function copyArray(arrOld) {
  let arrNew = []
  for(let i=0;i<arrOld.length;i++){
    let item = arrOld[i]
    let objItem = {}
    for (let key in item) {
      objItem[key] = item[key]
    }
    arrNew.push(objItem)
  }
  return arrNew
}

export function handleFormatDate (dateBefore, formatedType){
  let dateAfter = new Date(dateBefore);
  if (formatedType.toLowerCase().includes('datetime')) {
    dateAfter = dateAfter.toUTCString().substring(5, 25);
  } else {
    dateAfter = dateAfter.toUTCString().substring(5, 16);
  }
  return dateAfter;
};

export function deleteSeparator(number, separator) {
  while(number.includes(separator)){
    number = number.replace(separator,"")
  }
  return number
}

export function isNumeric(value) {
  return !isNaN(parseFloat(value)) && isFinite(value);
}

export function formatNumber(number) {
  number = number.toString().trim()
  number = deleteSeparator(number,",")
  let pjg = number.length
  if(!isNumeric(number)){
    pjg = pjg-1
    number = number.substr(0,pjg)
  }
  let tmp = ""
  if(pjg>3){
    while(pjg>3){
      pjg -= 3
      tmp = number.substr(pjg,3) + "." + tmp
    }
    if(pjg<=3){
      tmp = number.substr(0,pjg) + "." + tmp
    }
    tmp = tmp.substr(0, tmp.length-1)
  }else{
    tmp = number
  }

  return tmp
}
