import React from 'react';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem 
} from 'reactstrap';
import Logo from './../../support/img/logo.jpeg'
import {Link, Redirect} from 'react-router-dom'
import './../../support/css/header.css'
import {connect} from 'react-redux'
import {resetUser} from '../../1.actions/index'
import { getProfileUser } from './token';
import { checkPermission } from '../global/globalFunction';
import { Grid } from '@material-ui/core';
import globalConstant from './../global/globalConstant'


class Example extends React.Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      isOpen: true,
      isLogin:false,
      bankName: '',
    };
  }

  componentDidMount() {

    if(getProfileUser()) {
      const bank = JSON.parse(getProfileUser()) ;
      this.setState({bankName: bank.name})
    }
  }
  

  toggle() {
    this.setState({
      isOpen: !this.state.isOpen
    });
  }

  //Button log out function  
  logOutBtn =()=>{ 
    this.props.resetUser()  
    localStorage.clear();

    this.setState({isLogin: true})
  }

  renderMenu = () => {
    let tester = '';
    let menu =  globalConstant.dataMenu;


    tester = menu.map((menuPerBaris, index) => {
      if(menuPerBaris && menuPerBaris.child) {
        return (
          <UncontrolledDropdown nav inNavbar style={{marginBottom:'20px'}} key={index} className='menuDropDown' >
        
            <DropdownToggle nav caret className='menuDropDown' >
              <label>
                <Grid container className={'menuDropDown'}>
                  
                  <Grid item sm={3} xs={3} className={'menuDropDown'}>
                    {menuPerBaris.logo && <img src={require(`./../../icons/${menuPerBaris.logo}`)} alt='' width='50%' height='auto'/>}
                  </Grid>
                  <Grid item sm={9} xs={9}> {menuPerBaris.label} </Grid>
                </Grid>  
              </label>
            </DropdownToggle>              
                            
            
            <DropdownMenu className="menuDropDown">  
              <Grid container className="subMenuDropDown">
                {
                  menuPerBaris.child && menuPerBaris.child.map((menuChild, indexChild) => {
                    return (
                      <Grid container className="subMenuDropDown" style={{marginTop: '10px'}} key={`${menuPerBaris.label}-${indexChild}`}>
                        <Grid item sm={2} xs={2}></Grid>
                        <Grid item sm={10} xs={10}> 
                          <Link to={menuChild.link || '/'} style={{textDecoration:'none'}}>
                            <Grid container>
                              <Grid item sm={3} xs={3} className="subMenuDropDown">
                                {menuChild.logo &&<img src={require(`./../../icons/${menuChild.logo}`)} alt='' width='50%' height='auto'/>}
                              </Grid>
                              <Grid item sm={9} xs={9}>
                                <label>{menuChild.label}</label>
                              </Grid>
                            </Grid>
                            
                            
                          </Link>
                        </Grid>
                      </Grid>
                      
                    )
                  },this)
                }
              </Grid>                                
            </DropdownMenu>
          </UncontrolledDropdown>
        );
      } else {
        return(
          <Grid container key={index} className="menuDropDown" style={{marginBottom:'20px'}} >
            <Grid item sm={3} xs={3} className={'menuDropDown'}>
              {menuPerBaris.logo && <img src={require(`./../../icons/${menuPerBaris.logo}`)} alt='' width='50%' height='auto'/>}
            </Grid>
            <Grid item sm={9} xs={9}>
              <Link to={menuPerBaris.link || '/'}>
                <label>{menuPerBaris.label}</label>
              </Link>
            </Grid>
          </Grid>  
        );
        
      }
      
    }, this)

    

    return tester;
  }

  render() {
    if(this.state.isLogin){
      return(
        <Redirect to='/' />
      )
    }

    return (
      <div className="sideBar">
        <Navbar style={{textAlign:'center'}}>
          <NavbarBrand href="/" style={{ marginTop:"8px", color:"white",textDecoration:"none", textAlign:"center",fontSize:"20px", minWidth:'120px'}}>
              {/* <img src={Logo} alt="Logo" width="100%" /> */}
              <Grid container >
                <Grid item sm={12} xs={12}>
                  {this.state.bankName}
                </Grid>
              </Grid>
          </NavbarBrand>
          <NavbarToggler onClick={this.toggle} style={{display:"none"}}/>
          <Collapse isOpen={this.state.isOpen} navbar className='subMenuDropDown'>
            <Nav navbar>

                
              
              <p ></p>
              
              {this.renderMenu()}

              <Grid container onClick={this.logOutBtn} className={'menuDropDown'}>
                <Grid item sm={3} xs={3} className={'menuDropDown'}><i className="fas fa-sign-out-alt"></i> </Grid>
                <Grid item sm={9} xs={9}>
                  <label> Keluar </label>
                </Grid>
              </Grid>  

              {/* <p style={{ cursor:"pointer"}} onClick={this.logOutBtn}><label><i className="fas fa-sign-out-alt"></i> Log Out</label></p>
             */}
            </Nav>
          </Collapse>
        </Navbar>
      </div>
    );
  }
}
const mapStateToProp = (state)=>{
  return{
      name:state.user.name,
      address:state.user.address
  }
  
}
export default connect(mapStateToProp,{resetUser})(Example)
