import React from 'react';
import DatePicker from "react-date-picker";
import swal from 'sweetalert'
import { CSVLink } from "react-csv";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Loader from 'react-loader-spinner'
import "react-datepicker/dist/react-datepicker.css";
import {connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import './../../support/css/pagination.css'
import { getPermintaanPinjamanFunction, CSVDownloadFunction, confirmDisburseFunction, changeDisburseDateFunction } from './saga';
import 'moment/locale/id'
import { getTokenClient, getTokenAuth } from '../index/token';
import {checkPermission} from './../global/globalFunction'
import './../../support/css/table.css'
import TableComponent from './../subComponent/TableComponent'

const columnDataUser = [
  {
      id: 'id',
      numeric: false,
      label: 'ID Pinjaman',
  },
  {
      id: 'borrower_name',
      numeric: false,
      label: 'Nama Nasabah',
  },
  {
      id: 'service',
      numeric: false,
      label: 'Layanan',
  },
  {
    id: 'product',
    numeric: false,
    label: 'Produk',
  },
  {
    id: 'created_time',
    numeric: false,
    label: 'Tanggal Pengajuan',
    type:'datetime'
  },
  {
    id: 'approval_date',
    numeric: false,
    label: 'Tanggal Approval',
    type:'datetime'
  },
  {
    id: 'disburse_date',
    numeric: false,
    label: 'Tanggal Pencairan',
    type:'datetime'
  },
  {
    id: 'Konfirmasi',
    numeric: false,
    type: 'button',
    conditions: {
      disburse_status : 'processing',
      status : 'approved',
    },
    function: '',
    label: 'Telah Dicairkan',
  },
  {
    id: 'Ubah',
    numeric: false,
    type: 'button',
    conditions: {
      disburse_date_changed : false,
      disburse_status : 'processing',
    },
    label: 'Ubah Tanggal Pencairan',
    function: '',
  },

]

class PinjamanSetuju extends React.Component {
  state = {
    checkedData:[],
    rows: [],
    fromDate:null,
    toDate:null,
    page: 1,last_page:1,
    rowsPerPage: 10,
    isEdit: false,
    editIndex:Number,
    udahdiklik : false,
    startDate: new Date() ,endDate: new Date(),
    downloadDataCSV: [],downloadModal:false,
    total_data:0,
    loading:true,loadingBtn:false,
    searching:false,errorMessage:'',
    modal:false,idPinjaman:null,ubahTanggalPencairan:new Date(),disburse:false,telahDicairkan:'Dikonfirmasi',statusTanggalDisburse:'Diubah'
  };

  _isMounted =false

  componentDidMount(){
    this._isMounted=true;
    this.getAllData();

    columnDataUser[7].function = this.btnKonfirmasi;
    columnDataUser[8].function = this.ubahTanggalPencairanBtn;
  }

  componentWillUnmount(){
    this._isMounted=false
  }

  getAllData = async function (){
    const param ={
      status:"approved",
      rows:"10",
      page: this.state.page,
    }
    if(this.state.searching){
      param.start_date = this.state.fromDate
      param.end_date = this.state.toDate
    }


    const data = await getPermintaanPinjamanFunction(param)

    if(data){
      console.log(data.loanRequest.data)
      if(!data.error){
        this.setState({loading:false,rows:data.loanRequest.data,rowsPerPage:data.loanRequest.rows,page:data.loanRequest.current_page,last_page:data.loanRequest.last_page,total_data:data.loanRequest.total_data})        
      }else{
        this.setState({errorMessage:data.error})
      }
    }
  }

  //UNTUK TANGGAL 
  handleStartChange = (date)=> {
    this.setState({
      startDate: date
    });
  }

  handleEndChange = (date)=> {
    this.setState({
      endDate: date
    });
  }
  
  onBtnSearch = ()=>{
    this.setState({loading:true,searchModal:true,searching:true, checkedData:[]})
    const {startDate, endDate} = this.state

      var startMonth =''+ (startDate.getMonth()+1),
      startDay = '' + startDate.getDate(),
      startYear = startDate.getFullYear();

      var endMonth =''+ (endDate.getMonth()+1),
      endDay = '' + endDate.getDate(),
      endYear = endDate.getFullYear();

      if (startMonth.length < 2) startMonth = '0' + startMonth;
      if (startDay.length < 2) startDay = '0' + startDay;

      if (endMonth.length < 2) endMonth = '0' + endMonth;
      if (endDay.length < 2) endDay = '0' + endDay;
      
      var newFormatStartDate = startYear+"-"+startMonth+"-"+startDay+"T00:00:00.000Z"
      var newFormatEndDate = endYear+"-"+endMonth+"-"+endDay+"T23:59:59.000Z"
    
      if(endDate.getTime() < startDate.getTime() ){
        alert("Please input correct month range")
        this.setState({loading:false})
      }
      else{
        this.setState({fromDate:newFormatStartDate,toDate:newFormatEndDate,page:1},()=>{
            this.getAllData()
        })
        
      }
  }

  onBtnReset =()=>{
    this.setState({loading:true,searching:false,page:1},()=>{
      this.getAllData()

    })

  }

//BUAT DOWNLOAD CSV
  

  btnDownloadCsv = ()=>{
    this.setState({loadingBtn:true})

    const arr = this.state.checkedData;
  
    if (arr.length===0){
      swal("Info","Silahkan Pilih Data yang ingin di download","info")
      this.setState({loadingBtn:false})
    }else{
      var id=[]
      for (var j=0;j<arr.length;j++){
        id.push(arr[j])
      }
      const param = {
        id:id.toString()
      }
      this.btnCSVDownload(param)

    }    
  }

  btnCSVDownload = async function (param){
    const data = await CSVDownloadFunction(param)
    if(data){
      if(!data.error){
        this.setState({loadingBtn:false,downloadModal:true,downloadDataCSV:data.data})
      }else{
        this.setState({loadingBtn:false,downloadModal:false,errorMessage:data.error})
      }
    }
  }

  onChangePage = (current) => {
    this.setState({loading:true,page:current},()=>{
        this.getAllData()
    })
  }

  btnKonfirmasi = (id)=>{
      this.confirmDisburse({id})
  } 

  confirmDisburse = async function (param){
    const data = await confirmDisburseFunction (param)

    if(data){
      if(!data.error){
          this.setState({telahDicairkan:'Dikonfirmasi'}, () => { this.getAllData()})
          swal("Success","Dana telah dicairkan","success")
      }else{
          swal("Fail","Konfirmasi Gagal","error")
      }
    }
  }

  handleTanggalPencairan =(e)=>{
    this.setState({ubahTanggalPencairan:e})
  }
  
  handleCheckBox =(e)=>{
    const checkedData = this.state.checkedData;
    if(e.target.checked){
      checkedData.push(parseInt(e.target.value))
    }else{
      checkedData.splice(checkedData.indexOf(parseInt(e.target.value)),1)
    }

    this.setState({checkedData});
    
  }

  ubahTanggalPencairanBtn = ()=>{
    this.setState({disburse:true})
    const {ubahTanggalPencairan,idPinjaman} = this.state

    let startMonth =''+ (ubahTanggalPencairan.getMonth()+1),
    startDay = '' + ubahTanggalPencairan.getDate(),
    startYear = ubahTanggalPencairan.getFullYear();

    if (startMonth.length < 2) startMonth = '0' + startMonth;
    if (startDay.length < 2) startDay = '0' + startDay;

    const changeDate = startYear+"-"+startMonth+"-"+startDay
    const param ={
      id:idPinjaman,
      date:changeDate
    }
    
    this.disburseDate(param)
  }

  disburseDate = async function (param){
    const data = await changeDisburseDateFunction(param)

    if(data){
      if(!data.error){
        swal("Success","Tanggal pencairan berhasil diubah","success")
        this.setState({modal:false,disburse:false,statusTanggalDisburse:'Diubah'},()=>{
          this.getAllData()
        })
      }else{
        swal("Fail","Tanggal pencairan gagal diubah","error")
        this.setState({disburse:false})

      }
    }
  } 

  //FUNCTION BUAT MODAL
 
  btnCancelModalDownload = ()=>{
    this.setState({downloadModal:false,downloadDataCSV:[],searchModal:false})
    var checkboxes = document.querySelectorAll('input[type=checkbox]:checked')
    for (var i = 0; i < checkboxes.length; i++) {
      checkboxes[i].checked = false;
    }
      this.setState({checkedData: []})
  }

  renderBtnOrLoading =()=>{
    if (this.state.loadingBtn){
        return  <Loader 
        type="ThreeDots"
        color="#00BFFF"
        height="30"	
        width="30"
        
     />   
    }
    else{
        return(
          <input type="button" className="btn btn-primary ml-3" onClick={this.btnDownloadCsv} value="CSV Download"></input>
        )
    }
}
    renderBtnDisburseDate =()=>{
      if(this.state.disburse){
        return(
        <Button color="primary" onClick={this.ubahTanggalPencairanBtn}>   
            <Loader 
             type="ThreeDots"
             color="#00BFFF"
             height="30"	
             width="30"
         />       
        </Button>  
        )
      }else{
        return(
          <Button color="primary" onClick={this.ubahTanggalPencairanBtn}>Simpan</Button>    

        )
      }
    }

    
  render() {
    if(getTokenClient() && getTokenAuth()){
    return (
        <div className="container" style={{padding:0}}>

        <Modal isOpen={this.state.downloadModal} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Jumlah Download CSV: {this.state.checkedData.length} item(s)</ModalHeader>
          <ModalBody>
          <CSVLink data={this.state.downloadDataCSV} 
          > Click Here to Download CSV </CSVLink>
          </ModalBody>
          <ModalFooter>
            <Button color="secondary" onClick={this.btnCancelModalDownload}>Close</Button>
          </ModalFooter>
        </Modal>
      
        <Modal isOpen={this.state.modal} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Ubah Tanggal Pencairan</ModalHeader>
          <ModalBody>

            <DatePicker
              className="ml-3"
              format="yyyy-MM-dd"
              onChange={this.handleTanggalPencairan}
              value={this.state.ubahTanggalPencairan}
              clearIcon={null}
            /> 
            
                  
            
          </ModalBody>
          <ModalFooter>

            {this.renderBtnDisburseDate()}
            <Button color="secondary" onClick={()=>this.setState({modal:false})}>Tutup</Button>
          </ModalFooter>
        </Modal>

       
          < TableComponent
            id={"id"}
            title={'Pinjaman Disetujui - List'}
            checkBoxAction={this.handleCheckBox}
            searchDate={
              {
                value:[this.state.fromDate, this.state.toDate],
                label: 'Tanggal Approval',
                function: [this.onBtnSearch, this.onBtnReset],
                button: [
                  {
                    label:'Cari',
                    color:'#20B889',
                    function:this.onBtnSearch
                  },
                  {
                    label:'Reset',
                    color:'#EE6969',
                    function:this.onBtnReset
                  },
                ]
              }
            }
            arrayCheckBox={this.state.checkedData}
            paging={this.state.paging}
            loading={this.state.loading}
            columnData={columnDataUser}
            data={this.state.rows}
            page={this.state.page}
            rowsPerPage={this.state.rowsPerPage}
            totalData={this.state.total_data}
            onChangePage={this.onChangePage}     
            button={
              [
                {
                  label:'Download CSV',
                  color: '#20B889',
                  function:this.btnDownloadCsv
                },
              ]
            }        
            permissionDetail={ checkPermission('lender_loan_request_detail') ? '/permintaanpinjamanDetail/' : null}
          /> 
        
        </div>
    );
}
else if (getTokenAuth()){
  return  <Redirect to='/login' />
}
  }
}

const mapStateToProp = (state)=>{
  return{
      role: state.user.role,
      id: state.user.id
  }
}
export default connect (mapStateToProp)(PinjamanSetuju) ;
