import axios from 'axios'
import Cookie from 'universal-cookie'
import {serverUrl} from '../url'
const kukie = new Cookie()



export async function getPermintaanPinjamanFunction (param , next){
    return new Promise (async (resolve)=>{
        const config = {
            headers: {'Authorization': "Bearer " + kukie.get('tokenClient')}
        };
       
        let filter =''

        for (const key in param){
            filter += `&${key}=${param[key]}`
        }

      axios.get(serverUrl+`lender/loanrequest_list?orderby=id&sort=DESC${filter}`,config)
      .then((res)=>{
          param.loanRequest = res.data
          param.config = res.config.url
        if(next){
            resolve(next(param))
        }
            resolve(param)
         
      })
      .catch((err)=>{
        const error = err.response && err.response.data && err.response.data.message && `Error : ${err.response.data.message.toString().toUpperCase()}`
        param.error = error;
        resolve(param);
        })
    })
}

export async function getPermintaanPinjamanDetailFunction (param, status, next) {
    return new Promise(async (resolve)=>{
        const config = {
            headers: {'Authorization': "Bearer " + kukie.get('tokenClient')}
          };
          let newLink = serverUrl+`lender/loanrequest_list/${param.id}/detail`;

          if(status && status.toString().trim().length !== 0) {
              if(status === 'tolak') {
                newLink += `/reject?reason=${param.reason}`
              } else {
                newLink += `/approve?disburse_date=${param.date}`
              }
          }
          
        axios.get(newLink,config)
        .then((res)=>{
            param.dataLender = res.data
            if(next){
                resolve(next(param))
            }
            resolve(param)
        })
        .catch((err)=>{
            const error = err.response && err.response.data && err.response.data.message && `Error : ${err.response.data.message.toString().toUpperCase()}`
            param.error = error;
            resolve(param);
        })
    })
    
}

export async function CSVDownloadFunction (param){
    return new Promise(async (resolve)=>{
        const config = {
        headers: {'Authorization': "Bearer " + kukie.get('tokenClient')}
        };
        axios.get(serverUrl+`lender/loanrequest_list/download?id=${param.id}`,config)
        .then((res)=>{
            resolve(res)
        })
        .catch((err)=>{
        const error = err.response && err.response.data && err.response.data.message && `Error : ${err.response.data.message.toString().toUpperCase()}`
        param.error = error;
        resolve(param);
        })
    })
}

export async function getDetailDataBorrowerFunction (param,next){
    return new Promise(async (resolve)=>{
        const config = {
        headers: {'Authorization': "Bearer " + kukie.get('tokenClient')}
        };
        axios.get(serverUrl+`lender/borrower_list/${param.idBorrower}/detail`,config)
        .then((res)=>{
            param.dataBorrower = res.data
            if(next){
                resolve(next(param))
            }else{
                resolve(param)
            }
        })
        .catch((err)=>{
            const error = err.response && err.response.data && err.response.data.message && `Error : ${err.response.data.message.toString().toUpperCase()}`
            param.error = error;
            resolve(param);
        })
    })
}
