import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/styles";
import classNames from "classnames";
import Drawer from "@material-ui/core/Drawer";
import List from "@material-ui/core//List";
import Divider from "@material-ui/core/Divider";
import { ListItem, ListItemIcon, ListItemText } from "@material-ui/core/";
import ExpandLess from "@material-ui/icons/ExpandLess";
import ExpandMore from "@material-ui/icons/ExpandMore";
import Collapse from "@material-ui/core/Collapse";
import globalConstant from './../global/globalConstant'
import LoginBg from "./../../icons/asira.png";

const drawerWidth = 260;

const styles = theme => ({
  root: {
    flexGrow: 1,
    height: "100%",
    zIndex: 1,
    overflow: "hidden",
    position: "relative",
    display: "flex"
  },
  drawerPaper: {
    position: "relative",
    whiteSpace: "nowrap",
    width: drawerWidth, 
    // transition: theme.transitions.create("width", {
    //   easing: theme.transitions.easing.sharp,
    //   duration: theme.transitions.duration.enteringScreen
    // }),
    overflowY: "auto"
  },
  drawerPaperClose: {
    overflowX: "hidden",
    // transition: theme.transitions.create("width", {
    //   easing: theme.transitions.easing.sharp,
    //   duration: theme.transitions.duration.leavingScreen
    // }),
    width: "0px",
    // [theme.breakpoints.up("sm")]: {
    //   width: "0px"
    // }
  },
  loggedContent: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.default,
    paddingLeft: theme.spacing.unit * 3,
    paddingRight: theme.spacing.unit * 3,
    paddingBottom: theme.spacing.unit * 3,
    overflowX: "auto",
    height: "100vh"
  },
  content: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.default,
    paddingLeft: theme.spacing.unit * 3,
    paddingRight: theme.spacing.unit * 3,
    paddingBottom: theme.spacing.unit * 3,
    overflowX: "auto",
    height: "100vh",
    backgroundImage: `url(${LoginBg})`,
    backgroundSize: "100% 100%"
  },
  nested: {
    paddingLeft: theme.spacing.unit * 9
  },
  nestedWithWhiteSpace: {
    paddingLeft: theme.spacing.unit * 9,
    whiteSpace: "normal"
  },
  nestedChild: {
    paddingLeft: theme.spacing.unit * 12
  },
  nestedChildWithWhiteSpace: {
    paddingLeft: theme.spacing.unit * 12,
    whiteSpace: "normal"
  },
  normalWhiteSpace: {
    whiteSpace: "normal"
  }
});

class Draw extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fetch: false
    };
  }

  componentWillReceiveProps() {
    if (!this.state.fetch && this.props.user) {
      const userMenu = this.filterMenu();
      const collapse = {};
      const countPromises = [];
      for (const key in userMenu) {
        if (userMenu[key].imagePath && userMenu[key].imagePath !== "-") {
          countPromises.push();
        } else {
          countPromises.push();
        }
        if (userMenu[key].child) {
          collapse[userMenu[key].name] = false;
          for (const childKey in userMenu[key].child) {
            const childMenu = userMenu[key].child[childKey];
            if (childMenu.child) {
              collapse[childMenu.name] = false;
            }
          }
        }
      }
      Promise.all(countPromises).then(value => {
        let defaultImage = "";
        value.forEach(data => {
          const image = JSON.parse(data.config.data).image;
          const imageName = image;
          const extension = image.substring(
            image.lastIndexOf(".") + 1,
            image.length
          );
          if (!defaultImage && imageName === "default-image.png") {
            defaultImage = data.data;
          }
          for (const key in userMenu) {
            if (
              userMenu[key].imagePath &&
              userMenu[key].imagePath === imageName
            ) {
              userMenu[key].imagePath = data.data;
              userMenu[key].extension = extension;
            } else if (
              !userMenu[key].imagePath ||
              userMenu[key].imagePath === "-"
            ) {
              userMenu[key].imagePath = defaultImage;
              userMenu[key].extension = extension;
            }
          }
        });
        this.setState({
          ...collapse,
          userMenu,
          fetch: true
        });
      });
    }
  }

  handleDrawerOpen = () => {
    this.setState({ open: true });
  };

  handleDrawerClose = () => {
    this.setState({ open: false });
  };

  handleOnClick = menu => {
    // if (menu.name === 'Feedback Corner'){
    //   if(this.props.user.idBidang === 10) {
    //     this.props.handleRoute('/Feedback')
    //   }
    //   else{
    //     this.props.handleRoute('/Feedback/Create')
    //   }
    // }
    // else
    if (this.state[menu.name] !== undefined) {
      this.setState({
        [menu.name]: !this.state[menu.name]
      });
    } else {
      this.props.handleRoute(menu.route);
    }
  };

  filterMenu = () => {
    const { menu, user } = this.props;
    if (user && menu) {
      const access = this.props.user.roleAccess;

      // Get user menu
      const userMenu = [];
      for (let i = 0; i < menu.length; i++) {
        for (let j = 0; j < access.length; j++) {
          if (menu[i].id === access[j].idMenu) {
            menu[i].access = access[j].access;
            userMenu.push(menu[i]);
          }
        }
      }

      localStorage.setItem("menuAll", JSON.stringify(menu));
      // Sort menu
      const userMenuSorted = userMenu.sort((a, b) => a.parentId - b.parentId);
      const fixedMenu = {};
      userMenuSorted.forEach(data => {
        if (data.parentId === 0) {
          fixedMenu[data.id] = data;
        } else {
          for (const key in fixedMenu) {
            if (fixedMenu[key].id === data.parentId) {
              fixedMenu[key].toggle = false;
              if (!fixedMenu[key].child) {
                fixedMenu[key].child = {};
              }
              fixedMenu[key].child[data.id] = data;
            } else {
              for (const childKey in fixedMenu[key].child) {
                const child = fixedMenu[key].child[childKey];
                if (child.id === data.parentId) {
                  child.toggle = false;
                  if (!child.child) {
                    child.child = {};
                  }
                  child.child[data.id] = data;
                }
              }
            }
          }
        }
      });
      return fixedMenu;
    }
    return null;
  };

  renderCollapse = child => {
    if (child) {
      return <ExpandLess />;
    } else if (child === undefined) {
      return null;
    } else if (!child) {
      return <ExpandMore />;
    }
  };

  // Get image by file name
  getImage = userMenu =>
    `data:image/${userMenu.extension};base64,${userMenu.imagePath}`;

  render() {
    const { classes, open } = this.props;
    const userMenu = this.state.userMenu;
    return (
      <div className={classes.root}>
        {this.props.user && (
          <Drawer
            variant="permanent"
            classes={{
              paper: classNames(
                classes.drawerPaper,
                !open && classes.drawerPaperClose
              )
            }}
            className={classes.loggedContainer}
            open={this.props.open}
            // onMouseOver={this.handleDrawerOpen}
            // onMouseLeave={this.handleDrawerClose}
          >
            <Divider />
            <List>
              <ListItem>

              </ListItem>



            </List>
            <br />
            <br />
            <br />
          </Drawer>
        )}
        <main
          className={!this.props.user ? classes.content : classes.loggedContent}
        >
          {this.props.children}
          <br />
          <br />
          <br />
        </main>
      </div>
    );
  }
}

Draw.propTypes = {
  classes: PropTypes.object.isRequired,
  theme: PropTypes.object.isRequired,
  history: PropTypes.object.isRequired,
  open: PropTypes.bool.isRequired
};

export default withStyles(styles, { withTheme: true })(Draw);
