import React from 'react';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem } from 'reactstrap';

import Logo from './../support/img/logo.jpeg'
import {Link} from 'react-router-dom'
import './../support/css/header.css'
import {connect} from 'react-redux'
import {resetUser} from './../1.actions/index'
import Cookies from 'universal-cookie';

const kukie =new Cookies()


 class Example extends React.Component {

  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      isOpen: true
    };
  }
  toggle() {
    this.setState({
      isOpen: !this.state.isOpen
    });
  }
  //Button log out function  
  logOutBtn =()=>{ 
    this.props.resetUser()  
    kukie.remove("tokenClient")
  

  }
  render() {
    return (
      <div className="sideBar">
        <Navbar>
          <NavbarBrand href="/">
              <img src={Logo} alt="Logo" width="100%" />
          </NavbarBrand>
          <NavbarToggler onClick={this.toggle} style={{display:"none"}}/>
          <Collapse isOpen={this.state.isOpen} navbar>
            <Nav navbar>
              
            <p style={{ marginTop:"8px",marginRight:"10px",color:"inherit",textDecoration:"none", textAlign:"center",fontSize:"20px"}}>{this.props.name}</p>
            <Link to="/profileNasabah">
              <label><i className="fas fa-user-friends"></i> Nasabah</label>
              </Link>
              
              <UncontrolledDropdown  nav inNavbar>
                <DropdownToggle nav caret style={{ color:"inherit",textDecoration:"none"}}><label><i className="fas fa-hand-holding-usd"></i> Pinjaman</label></DropdownToggle>
                <DropdownMenu className="menuDropDown" style={{border:"1px solid black",marginBottom:"20px"}}>
                  <DropdownItem>
                 <Link to="/permintaanpinjaman" style={{ color:"inherit",textDecoration:"none"}}>
                  Permintaan Pinjaman
                   </Link>                   
                  </DropdownItem>
                  <DropdownItem>
                  <Link to="/pinjamansetuju"  style={{ color:"inherit",textDecoration:"none"}}>
                  Pinjaman - Disetujui
                  </Link>                
                  </DropdownItem>
                  <DropdownItem>
                  <Link to="/pinjamanrejected" style={{ color:"inherit",textDecoration:"none"}}>
                  Pinjaman - Tidak Disetujui</Link>                
                  </DropdownItem>
                </DropdownMenu>
              </UncontrolledDropdown>
              <p style={{ cursor:"pointer"}} onClick={this.logOutBtn}><label><i className="fas fa-sign-out-alt"></i> Log Out</label></p>

            </Nav>
          </Collapse>
        </Navbar>
      </div>
    );
  }
}
const mapStateToProp = (state)=>{
  return{
      name:state.user.name,
      address:state.user.address
  }
  
}
export default connect(mapStateToProp,{resetUser})(Example)
