import React from 'react'
import { Redirect } from 'react-router-dom'
import Moment from 'react-moment'
import swal from 'sweetalert';
import DatePicker from "react-date-picker";
import { getPermintaanPinjamanDetailFunction } from './saga';
import Loader from 'react-loader-spinner'
import { checkPermission, handleFormatDate,formatNumber } from './../global/globalFunction'
import { getTokenAuth, getTokenClient } from '../index/token';
import GridDetail from './../subComponent/GridDetail'
import Grid from '@material-ui/core/Grid'
import TitleBar from '../subComponent/TitleBar';


class Main extends React.Component{
    state = {errorMessage:'',rows:{},items:[],borrowerDetail:{},status:'',endDate:null,diterima:false,ditolak:false,productInfo:'',loading:true,dateApprove:null,reason:null}

    formatMoney=(number)=>
    { return number.toLocaleString('in-RP', {style : 'currency', currency: 'IDR'})}

    componentDidMount(){
        this.getDataDetail()
    }
    componentWillReceiveProps(newProps){
        this.setState({errorMessage:newProps.error})
    }
    getDataDetail =()=>{
        this.getDataDetailBtn()
    }
    getDataDetailBtn = async function(status){
        const param = {}
        param.idLoan = this.props.match.params.idLoan 
        param.idBorrower =  this.props.match.params.idBorrower

        if(status) {
            if(status === 'terima') {
                param.dateApprove = this.state.dateApprove
            } else if(status === 'tolak') {
                param.reason = this.state.reason
            }
        }
        const data = await getPermintaanPinjamanDetailFunction (param,status)
        if(data){
            if(!data.error){
                if(!status) {
                    this.setState({rows:data.dataLender,items:data.dataLender.fees,status:data.dataLender.status,borrowerDetail:data.dataLender.borrower_info,loading:false})
                } else if(status === 'terima'){
                    swal("Permintaan","Diterima","success")
                    this.setState({errorMessage:'',diterima:true})
                } else if(status === 'tolak'){
                    swal("Permintaan","Ditolak","warning")
                    this.setState({errorMessage:'',ditolak:true})
                }
                
            }else{
                this.setState({errorMessage:data.error})
            }
        }
    }
    findAmount = (dataFees, stringFee, amountPinjamanPokok, charPersen) => {
        let feeNew = '';
  
        if(dataFees && stringFee && amountPinjamanPokok) {
          for(const key in dataFees) {
            if(dataFees[key].description.toString().toLowerCase() === stringFee.toString().toLowerCase()) {
              if(dataFees[key].amount.toString().toLowerCase().includes('%')) {
                if(charPersen) {
                  feeNew = dataFees[key].amount;
                } else {
                  feeNew = parseInt(dataFees[key].amount) * amountPinjamanPokok / 100;
                }
              } else {
                if(charPersen) {
                  feeNew = parseInt(dataFees[key].amount)  * 100 / amountPinjamanPokok;
                } else {
                  feeNew = dataFees[key].amount;
                }
              }
            }
          }
        }
  
        return parseInt(feeNew);
      }
    
    handleEndChange = (date)=> {
        this.setState({
          endDate: date
        });
      }
    renderAdminFee = ()=>{
        var jsx = this.state.items.map((val,index)=>{
            return (
            <tr key={index}>
                <td>{val.description}</td>
                <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>
                <td>
                    {String(val.amount).includes("%")?
                     val.amount :
                     parseInt(val.amount)/parseInt(this.state.rows.loan_amount)*100 +"%"}
                </td>
                <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>
                <td>{String(val.amount).includes("%")  ?
                    this.formatMoney(parseInt(val.amount.slice(0,val.amount.indexOf("%")))*this.state.rows.loan_amount/100):
                    this.formatMoney(parseInt(val.amount))}</td>
            </tr>
            )
        })
        return jsx
    }
   
    
    btnTerimaPinjaman = ()=>{
        const {endDate} = this.state
        if (endDate === null){
            this.setState({errorMessage:"Tanggal Pencairan Kosong - Harap Cek Ulang"})
        }else{
            var endMonth =''+ (endDate.getMonth()+1),
            endDay = '' + endDate.getDate(),
            endYear = endDate.getFullYear();
            var newDay=''
            var newMonth=''
            if(parseInt(endDay)<10){
                newDay+= "0"+endDay
            }else{
                newDay = endDay
            }
            if(parseInt(endMonth)<10){
                newMonth+= "0"+endMonth
            }else{
                newMonth = endMonth
            }
            
            const newFormatEndDate = endYear+"-"+newMonth+"-"+newDay
            this.setState({dateApprove: newFormatEndDate},()=>{
                this.getDataDetailBtn('terima')
            });
        }
    }


    btnTolakPinjaman = ()=>{
        var reject = this.refs.alasanPenolakan.value

        if(!reject || reject.trim()===''){
            this.setState({errorMessage:"Alasan Penolakan Kosong - Harap Cek Ulang"})
        }else{
            this.setState({reason: reject}, () => { this.getDataDetailBtn('tolak')});
        }
      
    }

    btnBack = ()=>{
        window.history.back()
    }

    getBiayaAdmin =()=>{
        var jsx = this.state.items
        .map((val,index)=>{
            return (
                    <tr key={index}>
                    <td>{val.description}</td>
                    <td>: {this.formatMoney(parseInt(val.amount))}</td>
                    </tr>
            )
        })
         return jsx;
    }
    calculateConvienceFee = (percent)=>{
        var imbalhasil = this.state.rows.interest*this.state.rows.loan_amount/100
        var total = this.state.items.map((val)=>{
            return parseInt(val.amount) * this.state.rows.loan_amount/100
        })
        if(percent) {
            return (parseInt(this.state.rows.total_loan)-parseInt(this.state.rows.loan_amount)-parseInt(imbalhasil) - parseInt(total)) / parseInt(this.state.rows.loan_amount) * 100 
        } else {
            return this.formatMoney(parseInt(this.state.rows.total_loan)-parseInt(this.state.rows.loan_amount)-parseInt(imbalhasil) - parseInt(total))
        }
    }

    render(){
        if(this.state.loading){
            return(
                <Loader 
                type="ThreeDots"
                color="#00BFFF"
                height="30"	
                width="30"
                />  
            )
        }
        if(this.state.diterima){
            return (
                <Redirect to='/pinjamansetuju' />
            )   
        }
        
        if(this.state.ditolak){
            return (
                <Redirect to='/pinjamanrejected' />
            )   
        }
        if(getTokenAuth() && getTokenClient()){
            return(
              <Grid container className="containerDetail">
                      
                    <Grid item sm={12} xs={12} style={{maxHeight:50}}>
                        
                        <TitleBar
                            title={'Permintaan Pinjaman - Detail'}
                        />

                    </Grid>

                    <Grid
                        item
                        sm={12} xs={12}
                        style={{padding:10, marginBottom:20, boxShadow:'0px -3px 25px rgba(99,167,181,0.24)', WebkitBoxShadow:'0px -3px 25px rgba(99,167,181,0.24)', borderRadius:'15px'}}                  
                    >
                   <Grid container>
                   <Grid item sm={12} xs={12} style={{color:'red'}}>
                                {this.state.errorMessage}
                    </Grid>
                    {/* -----------------------------------------------------FIRST ROW----------------------------------------------------------------- */}
                    <GridDetail
                           gridLabel={[6,6,6]}
                           noTitleLine
                           background
                           label={[
                               ['ID Pinjaman','Nama Nasabah'],
                               ['Rekening Pinjaman','Status Pinjaman'],
                               ['Kategori', 'Agen/ AE'],
                           ]}
                           data={this.state.rows && [
                               [
                                this.state.rows.id,
                                this.state.rows.borrower_name
                               ],
                               [
                                this.state.rows.bank_account,
                                this.state.status &&   this.state.status === "processing"?
                                    {value:"Dalam Proses", color:'blue'}
                                    : this.state.status === "approved" && this.state.rows.disburse_status ==="confirmed"?
                                    {value:"Telah Dicairkan", color:'blue'}:
                                    this.state.status ==='rejected'?
                                    {value:"Ditolak", color:'red'}:
                                    this.state.status === 'approved'?
                                    {value:"Diterima", color:'green'}:
                                    null
                               ],
                               [
                                this.state.rows.category===""?"Personal":this.state.rows.category==="account_executive"?"Account Executive":"Agent",
                                `${this.state.rows.agent_name?this.state.rows.agent_name:"-"} (${this.state.rows.agent_provider_name?this.state.rows.agent_provider_name:"-"})`
                               ],
                           ]}                 
                     />

             {/* -----------------------------------------------------SECOND ROW----------------------------------------------------------------- */}
             <GridDetail
                           gridLabel={[6,6]}
                           label={[
                               ['Pinjaman Pokok','Tenor (Bulan)','Total Pinjaman','Angsuran Perbulan'],
                               ['Tujuan Pinjaman','Detail Tujuan','Tanggal Pengajuan',
                               this.state.status==='processing'?null:
                               this.state.status==='approved'?"Tanggal Persetujuan":
                               "Tanggal Ditolak"
                                ],
                           ]}
                           data={this.state.rows && [
                               [
                                   this.formatMoney(parseInt(this.state.rows.loan_amount)),
                                   this.state.rows.installment,
                                   this.formatMoney(parseInt(this.state.rows.total_loan)),
                                   this.formatMoney(parseInt(this.state.rows.layaway_plan)),
                               ],
                               [
                                this.state.rows.loan_intention,
                                this.state.rows.intention_details,
                                handleFormatDate(this.state.rows.created_time),
                                this.state.status==='processing'?null:handleFormatDate(this.state.rows.updated_time),
                                                   
                               ],
                           ]}                 
                     />

                 
                  {/* Fee Section */}
                <GridDetail
                  gridLabel={[6]}
                  noEquals
                  label={[
                    ['#','Imbal Hasil/ Bunga','Admin Fee','Convenience Fee'],
                    [
                        <b>(%)</b>,
                        this.state.rows.interest+ "%",
                        this.findAmount(this.state.rows && this.state.rows.fees, 'Admin Fee',this.state.rows && this.state.rows.loan_amount,true) +"%",
                        this.findAmount(this.state.rows && this.state.rows.fees, 'Convenience Fee',this.state.rows && this.state.rows.loan_amount,true)+ "%"
                    ],
                    [
                        <b>(Jumlah)</b>,
                        "Rp. "+formatNumber(this.state.rows && this.state.rows.loan_amount && this.state.rows.interest && parseInt(this.state.rows.interest * this.state.rows.loan_amount / 100), true),
                        "Rp. "+formatNumber(this.findAmount(this.state.rows && this.state.rows.fees, 'Admin Fee',this.state.rows && this.state.rows.loan_amount,false), true),
                        "Rp. "+formatNumber(this.findAmount(this.state.rows && this.state.rows.fees, 'Convenience Fee',this.state.rows && this.state.rows.loan_amount,false), true)
    
    
                    ],
                    ['','','','']
                   
                ]}
                data={this.state.rows && [
                    [' ',' ',' ',' '],
                    [' ',' ',' ',' '],
                    [' ',' ',' ',' ']
                  
                ]}   
                
                
                />


                
            
             {/* -----------------------------------------------------THIRD ROW----------------------------------------------------------------- */}
             <h2 className="mt-4">Info Penghasilan Saat Pengajuan</h2>
             <GridDetail
                           gridLabel={[3]}
                           label={[
                               ['Pendapatan perbulan','Penghasilan lain-lain (jika ada)','Sumber Penghasilan lain-lain']
                           ]}
                           data={this.state.rows && this.state.borrowerDetail&& [
                               [
                                this.state.borrowerDetail.monthly_income?
                                this.formatMoney(parseInt(this.state.borrowerDetail.monthly_income))
                                :0,
                                this.state.borrowerDetail.other_income?this.formatMoney(parseInt(this.state.borrowerDetail.other_income)):0,
                                this.state.borrowerDetail.other_incomesource
                               ],
                              
                           ]}                 
             />

            
             <div className="row">
                        <div className="col-12 col-md-12 ml-2">
                            <table style={{fontSize: '0.65em',marginBottom: '5px'}}>
                                <tbody>
                              
                                {this.state.status === "processing" ? 
                                 <tr>
                                 <td>Tanggal Pencairan</td>
                                 <td>:
                                   <DatePicker
                                     format="y-MM-dd"
                                     onChange={this.handleEndChange}
                                     value={this.state.endDate}
                                     clearIcon={null}
                                     className="pl-1"
                                 />
                                 </td>
                             </tr>
                                : this.state.status === "approved" ?
                                <tr>
                                <td>Tanggal Pencairan</td>
                                <td>: 
                                <Moment date={this.state.rows.disburse_date} format=" DD  MMMM  YYYY" />   
                                {this.state.rows.disburse_date_changed?
                                   <b>  (Telah Diubah)</b>
                                :null}                              

                                </td>
                             </tr>: this.state.status === "rejected" ?
                                    <tr>
                                    <td>Alasan Penolakan</td>
                                    <td>: {this.state.rows.reject_reason}  
                                    </td>
                                    </tr>
                            :
                            null
                            }
                            {this.state.status==='processing'?
                             <tr>
                                <td>Alasan Penolakan</td>
                                <td>
                                    : <input type="text" ref="alasanPenolakan" placeholder="Masukan alasan.."/>
                                </td>
                            </tr>
                            :null}
                        
                                </tbody>
                                
                            </table>
                        </div>
            </div>
             {/* -----------------------------------------------------FOURTH ROW----------------------------------------------------------------- */}
            
             <div className="row" style={{width:"100%"}}>
                        <div className="col-12 col-md-6">
                            <table>
                                <tbody>
                                    {
                                         this.state.status === "approved" || this.state.status === "rejected"? 
                                         <tr>
                                         <td >
                                             <input type="button" onClick={this.btnBack}  className="btn btn-primary" value="Kembali"></input>
                                         </td><td></td><td></td>
                            
                                     </tr>
                                         
                                         :
                                         <tr>
                                            {checkPermission("lender_loan_approve_reject") && <td>
                                                <input type="button" onClick={this.btnTerimaPinjaman} className="btn btn-success" value="Terima"></input>
                                            </td> }
                                            {checkPermission("lender_loan_approve_reject") &&<td>
                                                <input type="button" onClick={this.btnTolakPinjaman}  className="btn btn-danger mr-3 ml-3" value="Tolak"></input>
                                            </td> }
                                         <td>
                                             <input type="button" onClick={this.btnBack}  className="btn btn-primary" value="Kembali"></input>
                                         </td>
                                     </tr>
                                    }
                               
                                </tbody>
                                
                            </table>
                        </div>
            </div>
                       </Grid>
                    </Grid>
                </Grid>
            )
        }
        if(getTokenAuth()){
            return (
                <Redirect to='/login' />
            )    
        }
       
    }
}

export default Main;
