import React from 'react'
import Logo from './../../support/img/logo.jpeg'
import './../../support/css/login.css'
import Loader from 'react-loader-spinner'
import swal from 'sweetalert'
import {Redirect} from 'react-router-dom'
import {keepLogin} from '../../1.actions'
import {connect} from 'react-redux'
import { postLoginBankDashboardFunction, getTokenBorrowerFunction, getProfileFunction } from './saga'
import { Grid } from '@material-ui/core'


 
class Login extends React.Component{
    state={loading:false, isLogin : false}
  
    //LOGIN BUTTON
    btnLogin = ()=>{
        this.setState({loading:true})

        var username=this.refs.username.value
        var password=this.refs.password.value

        const param = {
            key: username,
            password
        }

        if (username==="" || password===""){
            swal("Error","Username and Password Empty","error")
            this.setState({loading:false})

        }else{
            this.postLogin(param)
        }
      
    } 
    
    postLogin = async function (param) {
        const data = await postLoginBankDashboardFunction(param, getTokenBorrowerFunction, getProfileFunction)

            if(data) {
                if(!data.error) {
                    this.setState({loading:false , isLogin : true})
                } else {
                    this.setState({loading:false})
                    swal("Gagal Login","Cek ulang Username dan Password","info")
                }
            }
    }

    renderBtnOrLoading =()=>{
        if (this.state.loading){
            return (
                <Loader 
                    type="Circles"
                    color="#00BFFF"
                    height="10"	
                    width="10"
                />   
            )
        }
        else{
            return(
                <input type="button" className="loginBtn" onClick={this.btnLogin} style={{marginTop:"20px"}} value="Sign in"/> 
            )
        }
    }

    render(){
        if(this.state.isLogin){
            return(
                <Redirect to='/' />
            )
        }
        // alert('masuk')
       

            return (
                <Grid container spacing={0}>
                    <Grid item sm={4} xs={4}>

                    </Grid>

                    <Grid item sm={4} xs={4}>
                        <Grid container spacing={0}> 
                            <Grid item sm={6} xs={6}>
                                <h5>Bank Dashboard</h5> 
                            </Grid>

                            <Grid item sm={6} xs={6}>
                                <img src={Logo} alt="logo" width="60%"></img>
                            </Grid>
                        </Grid>
                    </Grid>

                    <Grid item sm={4} xs={4}>

                    </Grid>
                </Grid>
                // <div className="App loginContainer mr-3">
                //     <div className="row">
                //         <div className="col-6 col-md-6 mt-3">
                //            <h5>Bank Dashboard</h5> 
                //         </div>
                //         <div className="col-6 col-md-6 mt-3">
                //             <img src={Logo} alt="logo" width="60%"></img>
                //         </div>
                //     </div>
    
                //     <div className="row">
                //         <div className="col-12 col-md-12">
                //             <hr/>
                //             <h2>Sign In</h2>
                //             <input type="text" placeholder="Username" ref="username" className="form-control"/>
                //             <input type="password" placeholder="Password" ref="password" className="form-control"/>
                //             {this.renderBtnOrLoading()}
                //             <p style={{marginTop:"5px"}}>Forgot your password?</p>
                       
                //         </div>
                //     </div>
                // </div>
            )

        }
      
       
    
}

export default connect(null,{keepLogin}) (Login);
