import React from 'react';
import axios from 'axios'
import {connect } from 'react-redux'
import Cookie from 'universal-cookie'
import { Redirect } from 'react-router-dom'
import {serverUrl} from '../url'
import {Link} from 'react-router-dom'
import {getPermintaanPinjamanFunction} from './saga'
import Loader from 'react-loader-spinner'
import QueryString from 'query-string'
import Pagination from 'rc-pagination';
import './../../support/css/pagination.css'
import localeInfo from 'rc-pagination/lib/locale/id_ID'

const kukie = new Cookie()
var config = {
  headers: {'Authorization': "Bearer " + kukie.get('tokenClient')}
};

class PinjamanRejected extends React.Component {
  state = {
    rows: [],detailNasabah:{}, searchRows:null,
    page: 1,
    rowsPerPage: 10,
    isEdit: false,
    editIndex:Number,
    udahdiklik : false,
    totalData:0,
    last_page:1,
    loading:true,
    halamanConfig:`orderby=id&sort=DESC&status=rejected&rows=10`

  };

  //-----------------------------------NIKO FUNCTION-------------------------------------------------------------
  

  _isMounted =false
  componentDidMount(){
    this._isMounted=true
    this.getAllData()
  }
  componentWillUnmount(){
    this._isMounted=false
  }
  getAllData = async function (){
    const param ={
      status:"rejected",
      rows:"10"
    }
    const data = await getPermintaanPinjamanFunction(param)

    if(data){
      console.log(data)
      if(!data.error){
        this.setState({loading:false,rows:data.data.data,rowsPerPage:data.data.rows,page:data.data.current_page,last_page:data.data.last_page,totalData:data.data.total_data})        
      }else{
        this.setState({errorMessage:data.error})
      }
    }
  }

  getLink = ()=>{
    var obj = QueryString.parse(this.props.location.search)
   return obj.query 
  }
  pushUrl = ()=>{
    var newLink ='/pinjamanrejected/search'
    var params =[]
    //categoryDropdown,search
    if(this.refs.search.value){
        params.push({
            params:'query',
            value:this.refs.search.value
        })
    }
    

    for (var i=0;i<params.length;i++){
        if(i===0){
            newLink += '?'+params[i].params+ '='+ params[i].value
        }else{
            newLink += '&'+params[i].params+ '='+ params[i].value
        }
    }
    this.props.history.push(newLink)
}

onChangePage = (current, pageSize) => {
  this.setState({loading:true})
  console.log('onChange:current=', current);
  console.log('onChange:pageSize=', pageSize);
  var newLink =`lender/loanrequest_list?`
  var searching = this.refs.search.value

  if(searching){
    //search function
    if(!isNaN(searching)){
      newLink += `id=${searching}&${this.state.halamanConfig}&page=${current}`
    }else{
      newLink += `owner_name=${searching}&${this.state.halamanConfig}&page=${current}`
    }
    
  }else{
      newLink += `${this.state.halamanConfig}&page=${current}`
  }
  axios.get(serverUrl+newLink,config)
  .then((res)=>{
      console.log(res.data)
      this.setState({loading:false,rows:res.data.data,page:current,rowsPerPage:res.data.rows,totalData:res.data.total_data})
  })
  .catch((err)=>{
      console.log(err)
  })
}

  onBtnSearch = ()=>{
   // this.pushUrl()
    var searching = this.refs.search.value
    this.setState({loading:true,searchRows:searching})
   
    const param ={
      status:"rejected",
      rows:"10"
    }
    if(searching){
      //search function
      if(!isNaN(searching)){
        param.id = searching
      }else{
        param.owner_name = searching
      }
      
    }
    this.btnSearching(param)
  }

  btnSearching = async function (param){
    const data = await getPermintaanPinjamanFunction(param)

    if(data){
      if(!data.error){
        this.setState({loading:false,rows:data.data.data,searchRows:null,rowsPerPage:data.data.rows,totalData:data.data.total_data})
      }else{
        this.setState({errorMessage:data.error})
      }
    }
  }

  renderJSX = ()=>{
    if (this.state.loading){
      return  (
        <tr  key="zz">
          <td align="center" colSpan={6}>
                <Loader 
            type="Circles"
            color="#00BFFF"
            height="40"	
            width="40"
        />   
          </td>
        </tr>
      )
  }else{
    if(this.state.rows.length===0){
      return(
        <tr key="asdf">
           <td align="center" colSpan={6}>Data empty</td>
        </tr>
      )
    }else{
      var jsx = this.state.rows.map((val,index)=>{
        return (
            <tr key={index}>
              <td align="center">{this.state.page >0 ? index+1 + (this.state.rowsPerPage*(this.state.page -1)) : index+1}</td>
              <td align="center">{val.id}</td>
              <td align="center">{val.owner_name}</td>
              <td align="center">{val.created_time.substr(0, val.created_time.indexOf('T'))}</td>
              <td align="center">
              <Link style={{textDecoration:"none"}} to={`/permintaanpinjamanDetail/${val.id}/${val.owner.Int64}`}>
              <input type="button" className="btn btn-primary" value="Details"></input>
              </Link>
              </td>
            </tr>
        )
    })
     return jsx;
    }
  }
   
  }
    
  render() {
   
  
if(kukie.get("tokenClient")&&kukie.get("token")){
      
    return (
        <div className="container">
           <div className="row">
                        <div className="col-6">
                             <h2 className="mt-3">Permintaan Ditolak</h2>
                        </div>
                        <div className="col-5 mt-3 ml-5">
                        <div className="input-group">
                            <input type="text" className="form-control" ref="search" placeholder="Search.." style={{width:"150px"}} />
                            <span className="input-group-addon ml-2" style={{border:"1px solid grey",width:"35px",height:"35px",paddingTop:"2px",borderRadius:"4px",paddingLeft:"2px",marginTop:"6px",cursor:"pointer"}} onClick={this.onBtnSearch}> 
                            <i className="fas fa-search" style={{fontSize:"28px"}} ></i></span>
                        </div>
                        </div>
          </div>
        <hr></hr>
          <table className="table table-hover">
          <thead className="table-warning">

              <tr >
                <th className="text-center" scope="col">#</th>
                <th className="text-center" scope="col">Id Pinjaman</th>
                <th className="text-center" scope="col">Nama Nasabah</th>
                <th className="text-center" scope="col">Tanggal Pengajuan</th>
                <th className="text-center" scope="col">Action</th>
              </tr>     
          </thead>
            <tbody>
  
            {this.renderJSX()}
  
            </tbody>
            
          </table>
          <hr></hr>
          <nav className="navbar" style={{float:"right"}}> 
                <Pagination className="ant-pagination"  
                showTotal={(total, range) => `${range[0]} - ${range[1]} of ${total} items`}
                total={this.state.totalData}
                pageSize={this.state.rowsPerPage}
                onChange={this.onChangePage}
                locale={localeInfo}
                />
          </nav>
        </div>
      

    );
  
  

}else if (kukie.get("token")){
  return  <Redirect to='/login' />
}
    
  }
}

const mapStateToProp = (state)=>{
  return{
     
      role: state.user.role,
      id: state.user.id
     
  }
  
}
export default connect (mapStateToProp)(PinjamanRejected) ;
