import React from 'react';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem 
} from 'reactstrap';
import Logo from './../../support/img/logo.jpeg'
import {Link, Redirect} from 'react-router-dom'
import './../../support/css/header.css'
import {connect} from 'react-redux'
import {resetUser} from '../../1.actions/index'
import { getProfileUser } from './token';
import { checkPermission } from '../global/globalFunction';

import ListSubheader from '@material-ui/core/ListSubheader';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import DraftsIcon from '@material-ui/icons/Drafts';
import SendIcon from '@material-ui/icons/Send';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import StarBorder from '@material-ui/icons/StarBorder';


class Example extends React.Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
        open: false,
        isOpen: true,
        isLogin:false,
        bankName: '',
    };
  }

  componentDidMount() {

    if(getProfileUser()) {
      const bank = JSON.parse(getProfileUser()) ;
      this.setState({bankName: bank.name})
    }
  }
  

  toggle() {
    this.setState({
      isOpen: !this.state.open
    });
  }

  //Button log out function  
  logOutBtn =()=>{ 
    this.props.resetUser()  
    localStorage.clear();

    this.setState({isLogin: true})
  }

  handleClick = () =>{
    this.setState({open: !this.state.open})
  }

  render() {
    if(this.state.isLogin){
      return(
        <Redirect to='/' />
      )
    }

    return (
        <List
            component="nav"
            aria-labelledby="nested-list-subheader"
            subheader={
                <ListSubheader component="div" id="nested-list-subheader">
                Nested List Items
                </ListSubheader>
            }
            className={classes.root}
        >
            <ListItem button>
                <ListItemIcon>
                <SendIcon />
                </ListItemIcon>
                <ListItemText primary="Sent mail" />
            </ListItem>
            <ListItem button>
                <ListItemIcon>
                <DraftsIcon />
                </ListItemIcon>
                <ListItemText primary="Drafts" />
            </ListItem>
            <ListItem button onClick={handleClick}>
                <ListItemIcon>
                <InboxIcon />
                </ListItemIcon>
                <ListItemText primary="Inbox" />
                {open ? <ExpandLess /> : <ExpandMore />}
            </ListItem>
            <Collapse in={open} timeout="auto" unmountOnExit>
                <List component="div" disablePadding>
                <ListItem button className={classes.nested}>
                    <ListItemIcon>
                    <StarBorder />
                    </ListItemIcon>
                    <ListItemText primary="Starred" />
                </ListItem>
                </List>
            </Collapse>
        </List>       
    );
  }
}
const mapStateToProp = (state)=>{
  return{
      name:state.user.name,
      address:state.user.address
  }
  
}
export default connect(mapStateToProp,{resetUser})(Example)
