import React from 'react';
import PropTypes from 'prop-types';
import localeInfo from 'rc-pagination/lib/locale/id_ID';
import Pagination from 'rc-pagination';
import {Link} from 'react-router-dom';
import Loader from 'react-loader-spinner';
import { formatNumber, handleFormatDate } from '../global/globalFunction';
import CheckBox from '@material-ui/core/Checkbox';
import './../../support/css/table.css'
import { Grid } from '@material-ui/core';
import TitleBar from './TitleBar';
import SearchBar from './SearchBar';
import DatePicker from "react-date-picker";
import "react-datepicker/dist/react-datepicker.css";

class TableComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: props.data,
      page: 1,
      rowsPerPage: 10,
    };
  }

  onChecked = (id, arrayCheckedBox) => {
    let flag = false;

    for(const key in arrayCheckedBox) {
      if(arrayCheckedBox[key].toString() === id.toString()) {
        flag = true;
        break;
      }
    }

    return flag;
  }

  render() {
    return (
      <Grid container>
        <Grid item sm={12} xs={12}>
          <TitleBar
            title={this.props.title}
          />
        </Grid>

        <Grid 
          item 
          sm={12} xs={12}
          style={{padding:20, marginBottom:20, boxShadow:'0px -3px 25px rgba(99,167,181,0.24)', WebkitBoxShadow:'0px -3px 25px rgba(99,167,181,0.24)', borderRadius:'15px'}}                                     
        >
          
          <Grid container>
            <Grid item sm={12} xs={12} style={{color:"red",fontSize:"15px",textAlign:'left'}}>
              {this.props.errorMessage}
            </Grid>

            <Grid item sm={this.props.searchDate ? 12 : 8} xs={12}>
              {
                this.props.searchDate &&
                <Grid container>
                  <Grid item sm={2} xs={12} style={{color:'#2076B8', fontSize:'6px'}}>
                    {this.props.searchDate.label}
                  </Grid>

                  <Grid item sm={5} xs={12}>
                    {
                      this.props.searchDate.value && 
                      <Grid container>

                        <Grid item sm={5} xs={5}>
                          <DatePicker
                            format="yyyy-MM-dd"
                            onChange={this.props.searchDate.value[0] || null}
                            value={(this.props.searchDate.function && this.props.searchDate.function[0]) || new Date()}
                            clearIcon={null}
                          />
                        </Grid>

                        { typeof(this.props.searchDate.value) === 'object' &&
                          <Grid item sm={1} xs={1}>
                            <hr style={{maxWidth:'10px',borderTop:'1px solid black'}}></hr>
                          </Grid>
                        }

                        { typeof(this.props.searchDate.value) === 'object' &&
                          <Grid item sm={5} xs={5}>
                            <DatePicker
                              format="yyyy-MM-dd"
                              onChange={this.props.searchDate.value[1] || null}
                              value={(this.props.searchDate.function && this.props.searchDate.function[1]) || new Date()}
                              clearIcon={null}
                            />
                          </Grid>
                        }
                        

                      </Grid>
                    }
                  </Grid>

                  <Grid item sm={5} xs={12}>

                  </Grid>

                </Grid>
              }
            </Grid>

            <Grid item sm={4} xs={12} style={{textAlign:'right'}}>
              {
                this.props.search &&
                <SearchBar
                  id="search"
                  value={this.props.search.value}
                  placeholder={this.props.search.label || 'Cari...'}
                  onChange={this.props.search.function || null} 
                />
              }
              
            </Grid>

            <Grid item sm={12} xs={12}>

              <table className="table table-hover">
                <thead className="theadCustom">
                  <tr >
                    {
                      this.props.checkBoxAction &&
                      <th className="text-center" scope="col" key={'CheckBox'}>
                        <i class="fas fa-check-square"></i>
                      </th>
                    }
                    <th className="text-center" scope="col" key={'#'}>#</th>
                    {
                      this.props.columnData.map((data,index) => {
                        return (
                          <th className="text-center" scope="col" key={index}>{data.label}</th>
                        );
                      },this) 
                    }
                    <th className="text-center" scope="col">Action</th>
                  </tr>     
                </thead>
                <tbody>
                  {
                    
                    this.props.loading &&
                    <tr  key="zz">
                      <td align="center" colSpan={this.props.columnData.length + 2}>
                        <Loader 
                          type="Circles"
                          color="#00BFFF"
                          height="40"	
                          width="40"
                        />   
                      </td>
                    </tr>
                  }

                  {
                    !this.props.loading && (!this.props.data || (this.props.data &&  this.props.data.length === 0 )) &&
                    <tr  key="failed"><td align="center" colSpan={this.props.columnData.length + 2}>{'No Data'}</td></tr>
                  }
                
                  {
                    !this.props.loading && this.props.data && this.props.data.length > 0 && this.props.data.map((dataTable,index) => {  

                      if(this.props.paging || (index >= ((this.props.page-1) * (this.props.rowsPerPage)) && index <= (this.props.rowsPerPage * this.props.page) - 1 )) {   
                        return (
                          <tr key={index}> 
                            {
                              this.props.checkBoxAction &&
                              <td align="center" style={{paddingTop: '0px'}}>
                                <CheckBox
                                  checked={this.onChecked(dataTable[this.props.id], this.props.arrayCheckBox)}
                                  onClick={this.props.checkBoxAction}
                                  value={dataTable[this.props.id]}
                                  color="default"
                                />
                              </td>
                            }
                          
                            <td align="center">{this.props.paging ? (index+1 + this.props.rowsPerPage*(this.props.page-1)) : index+1}</td>
                            {
                              this.props.columnData.map((dataRow, indexRow) => {                         
                                return(
                                  <td align={dataRow.numeric ? "right" : "center"} key={indexRow}>
                                    {
                                      dataRow.type && dataRow.type === 'datetime' ? handleFormatDate(dataTable[dataRow.id]) : 
                                    (dataRow.numeric ? formatNumber(dataTable[dataRow.id]) : (dataTable[dataRow.id] || '-'))}
                                  </td>
                                );               
                              }, this)
                            }
                            <td align="center">
                              { this.props.permissionEdit &&
                                <Link to={`${this.props.permissionEdit}${dataTable[this.props.id]}`} className="mr-2">
                                  <i className="fas fa-edit" style={{color:"black",fontSize:"18px"}}/>
                                </Link>
                              }
                              { this.props.permissionDetail &&
                                <Link to={`${this.props.permissionDetail}${dataTable[this.props.id]}`} >
                                  <i className="fas fa-eye" style={{color:"black",fontSize:"18px"}}/>
                                </Link>
                              }
                            </td>
                          

                          </tr>
                        )
                      } else {
                        return null
                      }
                    }, this)
                  }
                </tbody>
              </table>
              <hr></hr>
              <nav className="navbar" style={{float:"right"}}> 

                <Pagination className="ant-pagination"  
                  current={this.props.page}
                  showTotal={(total, range) => `${range[0]} - ${range[1]} of ${total} items`}
                  total={this.props.totalData}
                  showLessItems
                  pageSize={this.props.rowsPerPage}
                  onChange={this.props.onChangePage}
                  locale={localeInfo}
                />     
              </nav>
            </Grid>

          </Grid>
          

        </Grid>
        
      </Grid>
    );
  }
}

TableComponent.propTypes = {
  paging:PropTypes.bool,
  page: PropTypes.number,
  rowsPerPage: PropTypes.number,
  onChangePage: PropTypes.func,
  totalData: PropTypes.number,
  disabled: PropTypes.bool,
  permissionEdit: PropTypes.string,
  permissionDetail: PropTypes.string,
  id: PropTypes.string,
  loading: PropTypes.bool,
};

export default (TableComponent);
