import React from 'react';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem 
} from 'reactstrap';
import Logo from './../../support/img/logo.jpeg'
import {Link, Redirect} from 'react-router-dom'
import './../../support/css/header.css'
import {connect} from 'react-redux'
import {resetUser} from '../../1.actions/index'
import { getProfileUser } from './token';
import { checkPermission } from '../global/globalFunction';
import { Grid } from '@material-ui/core';
import globalConstant from './../global/globalConstant'


class Example extends React.Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      isOpen: true,
      isLogin:false,
      bankName: '',
    };
  }

  componentDidMount() {

    if(getProfileUser()) {
      const bank = JSON.parse(getProfileUser()) ;
      this.setState({bankName: bank.name})
    }
  }
  

  toggle() {
    this.setState({
      isOpen: !this.state.isOpen
    });
  }

  //Button log out function  
  logOutBtn =()=>{ 
    this.props.resetUser()  
    localStorage.clear();

    this.setState({isLogin: true})
  }

  renderMenu = () => {
    let tester = '';
    let menu =  globalConstant.dataMenu;
    console.log(menu)


      tester = menu.map((menuPerBaris) => {
        if(menuPerBaris && menuPerBaris.child) {
          return (
            <UncontrolledDropdown  nav inNavbar>
          
              <DropdownToggle nav caret className='menuDropDown' >
                <label>
                  <Grid container>
                    <Grid item sm={2} xs={2}><i className={menuPerBaris.logo}></i> </Grid>
                    <Grid item sm={8} xs={8}> {menuPerBaris.label} </Grid>
                  </Grid>  
                </label>
              </DropdownToggle>              
                             
              
              <DropdownMenu className="menuDropDown">  
                <Grid container style={{paddingLeft:'7px'}}>
                  {
                    menuPerBaris.child && menuPerBaris.child.map((menuChild) => {
                      return (
                        <Grid item sm={12} xs={12} > 
                          <Link to={menuChild.link} style={{textDecoration:'none'}}>
                            <DropdownItem className="subMenuDropDown"> {menuChild.label} </DropdownItem>
                          </Link>
                        </Grid>
                      )
                    },this)
                  }
                </Grid>                                
              </DropdownMenu>
            </UncontrolledDropdown>
          );
        } else if(menuPerBaris && !menuPerBaris.child)  {
          return(
            <Grid container>
              <Grid item sm={2} xs={2}><i className={menuPerBaris.logo}></i> </Grid>
              <Grid item sm={8} xs={8}>
                <Link to={menuPerBaris.link}>
                  <label><i className="fas fa-user-plus"></i> {menuPerBaris.label}</label>
                </Link>
              </Grid>
            </Grid>  
          );
          
        }
        
      }, this)

    

    return tester;
  }

  render() {
    if(this.state.isLogin){
      return(
        <Redirect to='/' />
      )
    }

    return (
      <div className="sideBar">
        <Navbar>
          <NavbarBrand href="/">
              {/* <img src={Logo} alt="Logo" width="100%" /> */}
          </NavbarBrand>
          <NavbarToggler onClick={this.toggle} style={{display:"none"}}/>
          <Collapse isOpen={this.state.isOpen} navbar>
            <Nav navbar>
              
              <p style={{ marginTop:"8px",marginRight:"10px",color:"white",textDecoration:"none", textAlign:"center",fontSize:"20px"}}>{this.state.bankName}</p>
              
              {this.renderMenu()}

              { checkPermission('lender_borrower_list') &&
                <UncontrolledDropdown  nav inNavbar>
                  
                  <DropdownToggle nav caret className='menuDropDown' >
                    <label>
                      <Grid container>
                        <Grid item sm={2} xs={2}><i className="fas fa-user-friends"></i> </Grid>
                        <Grid item sm={10} xs={10}> Nasabah </Grid>
                      </Grid>  
                    </label>
                  </DropdownToggle>                 
                  
                  <DropdownMenu className="menuDropDown">  
                    <Grid container style={{paddingLeft:'7px'}}>
                      <Grid item sm={12} xs={12} > 
                        <Link to="/profileNasabah" style={{textDecoration:'none'}}>
                          <DropdownItem className="subMenuDropDown">Nasabah List </DropdownItem>
                        </Link>
                      </Grid>
                      <Grid item sm={12} xs={12}> 
                        <Link to="/listCalonNasabah" style={{textDecoration:'none'}}>
                          <DropdownItem className="subMenuDropDown">Calon Nasabah List</DropdownItem>
                        </Link>
                      </Grid>
                    </Grid>                                
                  </DropdownMenu>
                </UncontrolledDropdown>
              }       
              
              { checkPermission('lender_loan_request_list') &&
                <UncontrolledDropdown  nav inNavbar>
                  <DropdownToggle nav caret style={{ color:"inherit",textDecoration:"none"}}><label><i className="fas fa-hand-holding-usd"></i> Pinjaman</label></DropdownToggle>
                  <DropdownMenu className="menuDropDown" style={{border:"1px solid black",marginBottom:"20px"}}>
                    <Link to="/permintaanpinjaman" style={{ color:"inherit",textDecoration:"none"}}>
                      <DropdownItem>  Permintaan Pinjaman </DropdownItem>
                    </Link>                   
                    <Link to="/pinjamansetuju"  style={{ color:"inherit",textDecoration:"none"}}>
                      <DropdownItem> Pinjaman - Disetujui </DropdownItem>
                    </Link>                
                    <Link to="/pinjamanrejected" style={{ color:"inherit",textDecoration:"none"}}>
                      <DropdownItem> Pinjaman - Tidak Disetujui </DropdownItem>
                    </Link> 
                  </DropdownMenu>
                </UncontrolledDropdown>
              }
              
              { checkPermission('lender_loan_request_list') &&
                <UncontrolledDropdown  nav inNavbar>
                  <DropdownToggle nav caret style={{ color:"inherit",textDecoration:"none"}}><label><i className="fas fa-file-invoice-dollar"></i> Pencairan </label></DropdownToggle>
                  <DropdownMenu className="menuDropDown" style={{border:"1px solid black",marginBottom:"20px"}}>                  
                    <Link to="/pencairanList"  style={{ color:"inherit",textDecoration:"none"}}>
                      <DropdownItem> Status Loan - Telah Dicairkan </DropdownItem>
                    </Link>       
                  </DropdownMenu>
                </UncontrolledDropdown>
              }

              <p style={{ cursor:"pointer"}} onClick={this.logOutBtn}><label><i className="fas fa-sign-out-alt"></i> Log Out</label></p>
            </Nav>
          </Collapse>
        </Navbar>
      </div>
    );
  }
}
const mapStateToProp = (state)=>{
  return{
      name:state.user.name,
      address:state.user.address
  }
  
}
export default connect(mapStateToProp,{resetUser})(Example)
