import React from 'react'
import Cookies from 'universal-cookie';
import { Redirect } from 'react-router-dom'
// import CheckBox from '../subComponent/CheckBox';
import Grid from '@material-ui/core/Grid'
import Loader from 'react-loader-spinner'
import { connect } from 'react-redux';
import { withRouter } from 'react-router';
import { withStyles } from '@material-ui/styles';
import { compose } from 'redux';
import { getPermintaanPinjamanDetailFunction, getDetailDataBorrowerFunction } from '../permintaanPinjaman/saga';
import { formatNumber, handleFormatDate } from '../global/globalFunction';

const styles = (theme) => ({
    container: {
      flexGrow: 1,
    },
  });

const cookie = new Cookies();

class rolePermissionDetail extends React.Component{
    _isMounted = false;
    
    state = {
      diKlik:false,
      errorMessage:'',
      listRolePermission: [],
      dataDetail: {},
      role : {},
      loanId: 0,
      disabled: true,
      loading: true,
    };

    componentDidMount(){
      this._isMounted = true;

      this.setState({
        loanId: this.props.match.params.idLoan,
      },() => {
        this.refresh();
      })
      
    }

    componentWillUnmount() {
      this._isMounted = false;
    }

    refresh = async function(){
      const param = {
        idLoan: this.state.loanId,
      };

      const data = await getPermintaanPinjamanDetailFunction(param, null, getDetailDataBorrowerFunction);

      if(data) {
        console.log(data)
          if(!data.error) {
            this.setState({
              dataDetail: data.dataLender,
              dataBorrower: data.dataBorrower,
              loading: false,
            })
          } else {
            this.setState({
              errorMessage: data.error,
              disabled: true,
              loading: false,
            })
          }      
      }
    }

    btnCancel = ()=>{
      this.setState({diKlik:true})
    }
    componentWillReceiveProps(newProps){
      this.setState({errorMessage:newProps.error})
    }

    checkingRole = (role, idRolePermission) => {
        for (const key in role) {
          if (
            role[key].id.toString().trim() ===
            idRolePermission.toString().trim()
          ) {
            return true;
          }
        }
        return false;
    }

    render(){
        if(this.state.diKlik){
            return <Redirect to='/pencairanList'/>            
        } else if (this.state.loading){
          return  (
            <div  key="zz">
              <div align="center" colSpan={6}>
                <Loader 
                  type="Circles"
                  color="#00BFFF"
                  height="40"	
                  width="40"
                />   
              </div>
            </div>
          )
        } else if(cookie.get('token')){
            return(
                <div className="container mt-4">
                  <h3>Role Permission - Detail</h3>
                  
                  <hr/>
                  {/* Title Section */}
                  <Grid container spacing={0} style={{paddingLeft: '2vh', marginBottom:'2vh', backgroundColor: '#E8E8E8'}}>
                    <Grid item sm={6} xs={6}>
                      <Grid container spacing={0}>
                        <Grid item sm={6} xs={12}>
                          ID Pinjaman
                        </Grid>
                        <Grid item sm={6} xs={12}>
                          : {this.state.dataDetail && this.state.dataDetail.id}
                        </Grid>
                      </Grid>
                    </Grid>
                    <Grid item sm={6} xs={6}>
                      <Grid container spacing={0}>
                        <Grid item sm={6} xs={12}>
                          Rekening Pinjaman
                        </Grid>
                        <Grid item sm={6} xs={12}>
                          : {this.state.dataBorrower && this.state.dataBorrower.bank_accountnumber}
                        </Grid>
                      </Grid>
                    </Grid>
                    <Grid item sm={6} xs={6}>
                      <Grid container spacing={0}>
                        <Grid item sm={6} xs={12}>
                          Nama Nasabah
                        </Grid>
                        <Grid item sm={6} xs={12}>
                          : {this.state.dataDetail && this.state.dataDetail.owner_name}
                        </Grid>
                      </Grid>
                    </Grid>
                    <Grid item sm={6} xs={6}>
                      <Grid container spacing={0}>
                        <Grid item sm={6} xs={12}>
                          Status Pinjaman
                        </Grid>
                        <Grid item sm={6} xs={12}>
                          : {this.state.dataDetail && this.state.dataDetail.status}
                        </Grid>
                      </Grid>
                    </Grid>
                  </Grid>

                  {/* Detail Section */}
                  <Grid container spacing={0} style={{paddingLeft: '2vh', marginBottom:'2vh'}}>

                    <Grid item sm={6} xs={12}>
                      <Grid container spacing={0}>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={12}>
                              Pinjaman Pokok
                            </Grid>
                            <Grid item sm={6} xs={12}>
                              : Rp {formatNumber(this.state.dataDetail && this.state.dataDetail.loan_amount, true)}
                            </Grid>
                          </Grid>
                        </Grid>
                      
                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={12}>
                              Tenor (Bulan)
                            </Grid>
                            <Grid item sm={6} xs={12}>
                              : {this.state.dataDetail && this.state.dataDetail.installment}
                            </Grid>
                          </Grid>
                        </Grid>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={12}>
                              Total Pinjaman
                            </Grid>
                            <Grid item sm={6} xs={12}>
                              : Rp {formatNumber(this.state.dataDetail && this.state.dataDetail.total_loan, true)}
                            </Grid>
                          </Grid>
                        </Grid>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={12}>
                              Angsuran Perbulan
                            </Grid>
                            <Grid item sm={6} xs={12}>
                              : Rp {formatNumber(this.state.dataDetail && this.state.dataDetail.layaway_plan, true)}
                            </Grid>
                          </Grid>
                        </Grid>

                      </Grid>
                    </Grid>

                    <Grid item sm={6} xs={12}>
                      <Grid container spacing={0}>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={12}>
                              Tujuan Pinjaman
                            </Grid>
                            <Grid item sm={6} xs={12}>
                              : {this.state.dataDetail && this.state.dataDetail.loan_intention}
                            </Grid>
                          </Grid>
                        </Grid>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={12}>
                              Detail Tujuan
                            </Grid>
                            <Grid item sm={6} xs={12}>
                              : {this.state.dataDetail && this.state.dataDetail.intention_details}
                            </Grid>
                          </Grid>
                        </Grid>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={12}>
                              Tanggal Pengajuan
                            </Grid>
                            <Grid item sm={6} xs={12}>
                              : {handleFormatDate(this.state.dataDetail && this.state.dataDetail.created_time)}
                            </Grid>
                          </Grid>
                        </Grid>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={12}>
                              Tanggal Persetujuan
                            </Grid>
                            <Grid item sm={6} xs={12}>
                              : {this.state.dataDetail && this.state.dataDetail.updated_time}
                            </Grid>
                          </Grid>
                        </Grid>

                      </Grid>
                    </Grid>            
                    
                  </Grid>

                  {/* Fee Section */}
                  <Grid container spacing={0} style={{paddingLeft: '2vh', marginBottom:'2vh'}}>

                    <Grid item sm={6} xs={12}>
                      <Grid container spacing={0}>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={4} xs={4}>
                              
                            </Grid>
                            <Grid item sm={4} xs={4} style={{textAlign: 'center'}}>
                              (%)
                            </Grid>
                            <Grid item sm={4} xs={4} style={{textAlign: 'center'}}>
                              (Jumlah)
                            </Grid>
                          </Grid>
                        </Grid>
                      
                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={4} xs={4}>
                              Imbal Hasil/ Bunga
                            </Grid>
                            <Grid item sm={4} xs={4} style={{textAlign: 'center'}}>
                              {this.state.dataDetail && this.state.dataDetail.interest} %
                            </Grid>
                            <Grid item sm={4} xs={4} style={{textAlign: 'center'}}>
                              Rp {formatNumber(this.state.dataDetail && this.state.dataDetail.loan_amount && this.state.dataDetail.interest && parseInt(this.state.dataDetail.interest * this.state.dataDetail.loan_amount / 100), true)}
                            </Grid>
                          </Grid>
                        </Grid>
                      
                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={4} xs={4}>
                              Admin Fee
                            </Grid>
                            <Grid item sm={4} xs={4} style={{textAlign: 'center'}}>
                              Rp {this.state.dataDetail && this.state.dataDetail.admin_fee}
                            </Grid>
                            <Grid item sm={4} xs={4} style={{textAlign: 'center'}}>
                              Rp {formatNumber(this.state.dataDetail && this.state.dataDetail.admin_fee, true)}
                            </Grid>
                          </Grid>
                        </Grid>
                      
                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={4} xs={4}>
                              Convenience Fee
                            </Grid>
                            <Grid item sm={4} xs={4} style={{textAlign: 'center'}}>
                              Rp {this.state.dataDetail && this.state.dataDetail.convenience_fee}
                            </Grid>
                            <Grid item sm={4} xs={4} style={{textAlign: 'center'}}>
                              Rp {formatNumber(this.state.dataDetail && this.state.dataDetail.convenience_fee, true)}
                            </Grid>
                          </Grid>
                        </Grid>
                      
                      </Grid>
                    </Grid>

                    <Grid item sm={6} xs={12}>
                    </Grid>            

                  </Grid>
                 
                  {/* Info Penghasilan Section */}
                  <Grid container spacing={0} style={{paddingLeft: '2vh', marginBottom:'2vh'}}>
                    <Grid item sm={12} xs={12}>
                      <h1> Info Penghasilan saat Pengajuan</h1>
                    </Grid>

                    <Grid item sm={6} xs={12}>
                      <Grid container spacing={0}>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={6}>
                              Pendapatan perbulan
                            </Grid>
                            <Grid item sm={6} xs={6}>
                              : Rp {formatNumber(this.state.dataDetail && this.state.dataDetail.pendapatan, true)}
                            </Grid>
                          </Grid>
                        </Grid>
                      
                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={6}>
                              Penghasilan lain-lain (jika ada)
                            </Grid>
                            <Grid item sm={6} xs={6}>
                              : Rp {formatNumber(this.state.dataDetail && this.state.dataDetail.pendapatan_lain, true)}
                            </Grid>
                          </Grid>
                        </Grid>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={6}>
                              Sumber Penghasilan lain-lain
                            </Grid>
                            <Grid item sm={6} xs={6}>
                              : Rp {formatNumber(this.state.dataDetail && this.state.dataDetail.sumber_pendapatan_lain, true)}
                            </Grid>
                          </Grid>
                        </Grid>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={6}>
                              Tanggal Pencairan
                            </Grid>
                            <Grid item sm={6} xs={6}>
                              : {this.state.dataDetail && this.state.dataDetail.disburse_date}
                            </Grid>
                          </Grid>
                        </Grid>

                      </Grid>
                    </Grid>

                    <Grid item sm={6} xs={12}>
                    </Grid>            

                  </Grid>
                 

                </div>
            )
        } else if(!cookie.get('token')){
          return (
              <Redirect to='/login' />
          )    
        }
       
    }
}

export function mapDispatchToProps(dispatch) {
    return {
    //   getSourceTransaction: () => {
    //     dispatch(sourceTransactionRequest());
    //   },
    //   handleRedirect: (route) => {
    //     dispatch(push(route));
    //   },
    };
}
  
export const mapStateToProps = (state)=>{
  return{  
    // role: state.user.role,
    // id: state.user.id  
  }
  
}

const withConnect = connect(
    mapStateToProps,
    mapDispatchToProps
);

const withStyle = withStyles(styles);

export default compose(
    withConnect,
    withStyle,
    withRouter
  )(rolePermissionDetail);
