import React from 'react';
import {connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import {Link} from 'react-router-dom'
import Loader from 'react-loader-spinner'
import Pagination from 'rc-pagination';
import './../../support/css/pagination.css'
import localeInfo from 'rc-pagination/lib/locale/id_ID'
import {getPermintaanPinjamanFunction} from './saga'
import Moment from 'react-moment'
import 'moment/locale/id';
import { getTokenClient, getTokenAuth } from '../index/token';
import SearchBar from './../subComponent/SearchBar';

class PermintaanPinjaman extends React.Component {
  state = {
    rows: [],detailNasabah:{}, searchRows:'',
    page: 1,
    rowsPerPage: 10,
    isEdit: false,
    editIndex:Number,
    udahdiklik : false,
    totalData:0,
    last_page:1,
    loading:true,
    errorMessage:''
  };

  //-----------------------------------NIKO FUNCTION-------------------------------------------------------------
  _isMount = false
  componentDidMount(){
    this._isMount=true
    this.getPermintaanPinjamanBtn()
  }
  componentWillUnmount(){
    this._isMount=false
  }
  componentWillReceiveProps(newProps){
    this.setState({errorMessage:newProps.error})
  }

  getPermintaanPinjamanBtn = async function () {
    let params={ 
      status:"processing",
      rows:"10",
      page:this.state.page
    }
    let hasil = this.state.searchRows
    if(hasil){
      //search function
      params.search_all = hasil
    }

    const data = await getPermintaanPinjamanFunction(params)
    if(data){
      console.log(data)
      if(!data.error){
        this.setState({loading:false,rows:data.loanRequest.data,rowsPerPage:data.loanRequest.rows,totalData:data.loanRequest.total_data,last_page:data.loanRequest.last_page,page:data.loanRequest.current_page})
      }else{
        this.setState({errorMessage:data.error})
      }
    }
  }

  onBtnSearch = (e)=>{
      this.setState({loading:true,searchRows:e.target.value,page:1},()=>{
        this.getPermintaanPinjamanBtn()
      })
  }

  onChangePage = (current) => {
    this.setState({loading:true,page:current},
      ()=>{
        this.getPermintaanPinjamanBtn()
      })
  }

  renderJSX = ()=>{
    if (this.state.loading){
      return  (
        <tr>
          <td align="center" colSpan={10}>
                <Loader 
            type="Circles"
            color="#00BFFF"
            height="40"	
            width="40"
        />   
          </td>
        </tr>
      )
  }else{
    if(this.state.rows.length===0){
      return(
        <tr>
           <td align="center" colSpan={10}>Data empty</td>
        </tr>
      )
    }else{
      var jsx = this.state.rows.map((val,index)=>{
        return (
            <tr key={index}>
              <td align="center">{this.state.page >0 ? index+1 + (this.state.rowsPerPage*(this.state.page -1)) : index+1}</td>
              <td align="center">{val.id}</td>
              <td align="center">{val.owner_name}</td>
              {/* <td align="center">{val.bank_name}</td> */}
              <td align="center">{val.category ==="account_executive"?"Account Executive" :val.category === "agent"?"Agent":"Personal"}</td>
              <td align="center">{val.service.toString()}</td>
              <td align="center">{val.product.toString()}</td>
              <td align="center"><Moment locale="id" date={val.created_time} format=" DD  MMMM  YYYY" /> </td>
              <td align="center" style={val.status==="processing"?{color:"blue"}:val.status==="approved"?{color:"green"}:{color:"red"}}>
                {val.status==="processing"?"Dalam Proses":val.status==="approved"?"Diterima":"Ditolak"}
              </td>
              <td align="center">
              <Link style={{textDecoration:"none"}} to={`/permintaanpinjamanDetail/${val.id}`}>
                  <i className="fas fa-eye" style={{color:"black",fontSize:"18px"}}/>
              </Link>
              </td>
            </tr>
        )
    })
     return jsx;
    }
  }
   
  }
    
  render() {
   
  if(getTokenClient() && getTokenAuth()){
      
    return (
        <div className="container">
         <div className="row">
                        <div className="col-6">
                             <h2 className="mt-3">Permintaan Pinjaman - List</h2> {this.state.errorMessage}
                        </div>
                        <div className="col-5 mt-3 ml-5">
                        <div className="input-group">
                        <SearchBar
                             onChange={this.onBtnSearch}
                             placeholder="Search Nama Nasabah, ID Pinjaman.."
                             value={this.state.searchRows}
                          />
                         
                        </div>
                        </div>
          </div>
        <hr></hr>
          <table className="table table-hover">
          <thead className="table-warning">

              <tr>
              <th className="text-center" scope="col">#</th>
              <th className="text-center" scope="col">ID Pinjaman</th>
              <th className="text-center" scope="col">Nama Nasabah</th>
              {/* <th className="text-center" scope="col">Bank Akun</th> */}
              <th className="text-center" scope="col">Kategori</th>
              <th className="text-center" scope="col">Layanan</th>
              <th className="text-center" scope="col">Produk</th>
              <th className="text-center" scope="col">Tanggal Pengajuan</th>
              <th className="text-center" scope="col">Status Pinjaman</th>
              <th className="text-center" scope="col">Action</th>
               
              </tr>     
          </thead>
            <tbody>
  
            {this.renderJSX()}
  
            </tbody>
            
          </table>
          <hr></hr>
          <nav className="navbar" style={{float:"right"}}> 
                <Pagination className="ant-pagination"  
                current={this.state.page}
                showTotal={(total, range) => `${range[0]} - ${range[1]} of ${total} items`}
                total={this.state.totalData}
                pageSize={this.state.rowsPerPage}
                onChange={this.onChangePage}
                locale={localeInfo}
                />
          </nav>
        </div>
      

    );
  
  

}
else if(getTokenAuth()){
  return  <Redirect to='/login' />
}
    
  }
}

const mapStateToProp = (state)=>{
  return{
     
      role: state.user.role,
      id: state.user.id
     
  }
  
}
export default connect (mapStateToProp)(PermintaanPinjaman) ;
