import React from 'react';
import './App.css';
import {Route,withRouter,Switch} from 'react-router-dom'
import {connect} from 'react-redux'
import {keepLogin} from './1.actions'
import {serverUrl} from './components/url'
import {serverUrlBorrower} from './components/url'



import PageNotFound from './components/404'
import Login from './components/login'
import Home from './components/main'
import Nasabah from './components/profileNasabah/profileNasabah'
import ScrollTop from './components/scrollToTop'
import Header from './components/index/header'
import Testing from './components/testing'
import profileNasabahDetail from './components/profileNasabah/profileNasabahDetail'
import PermintaanPinjaman from './components/permintaanPinjaman/permintaanPinjaman'
import PermintaanPinjamanDetail from './components/permintaanPinjaman/permintaanPinjamanDetail'
import PinjamanSetuju from './components/permintaanPinjaman/pinjamanSetuju'
import PinjamanRejected from './components/permintaanPinjaman/pinjamanRejected'
import PencairanList from './components/pencairan/pencairanList'
import PencairanDetail from './components/pencairan/pencairanDetail'
import Cookies from 'universal-cookie';
import axios from 'axios'

const kukie =new Cookies()
var tokenClient = kukie.get("tokenClient")

class App extends React.Component {
  state ={loading : true , tokenClient : null , Token : null}
 componentDidMount(){
  this.getAuth()
  this.getAuthBorrower()

  if (tokenClient){
   this.props.keepLogin()
   }

  }


  getAuthBorrower =()=>{
    var urlBorrow = serverUrlBorrower+"clientauth"
    axios.get(urlBorrow ,{
      auth : {
          username : 'androkey',
          password : 'androsecret'
      }
  })
      .then((res)=>{
          var date = new Date();
          date.setTime(date.getTime() + (res.data.expires*1000));
          kukie.set('tokenBorrower',res.data.token,{expires: date})
          
         
      })
      .catch((err)=>{
        console.log(err)
      }
         
      )
  }
 getAuth = ()=>{
  var url =serverUrl+"clientauth"
  axios.get(url ,{
      auth : {
          username : 'reactkey',
          password : 'reactsecret'
      }
  })
      .then((res)=>{
          var date = new Date();
          date.setTime(date.getTime() + (res.data.expires*1000));
          kukie.set('token',res.data.token,{expires: date})
          this.setState({loading : false})
         
      })
      .catch((err)=>{
        console.log(err)
        setTimeout(function(){ alert("Coba reload halaman/ cek koneksi internet"); }, 5000);
      }
         
      )
}


  render() {
    if(this.state.loading){
      return(
        <p> loading ....</p>
      )
    }
    
      return (
      
        <div>
          <ScrollTop>
            <div className="row">
            {
                kukie.get('tokenClient') ? 
              <div className="sidebar col-3 col-md-3">
              <Header/>
              </div>
              :
              null
            }
              <div className="sidebar col-8 col-md-8">
              <Switch> 
                    <Route path='/' component={Home} exact></Route>
                    <Route path='/profileNasabah' component={Nasabah}></Route>
                    <Route path="/profileNasabahDetail/:id" component={profileNasabahDetail}></Route>
                    <Route path="/permintaanpinjaman" component={PermintaanPinjaman}></Route>
                    <Route path="/permintaanpinjamanDetail/:idLoan" component={PermintaanPinjamanDetail}></Route>
                    <Route path='/tester' component={Testing}></Route>
                    <Route path='/pinjamansetuju' component={PinjamanSetuju}></Route>
                    <Route path='/pinjamanrejected' component={PinjamanRejected}></Route>
                    <Route path='/pencairanList' component={PencairanList}></Route>
                    <Route path='/pencairanDetail/:idLoan' component={PencairanDetail}></Route>
                    {kukie.get('tokenClient') ?  <Route path="/login" component={Home}></Route>:  <Route path="/login" component={Login}></Route>} 

                    <Route path='*' component={PageNotFound} />
              </Switch>
              </div>
            </div>
          </ScrollTop>
        </div>
     
         );
    
  
     
 
  }
}
  
const mapStateToProps = (state)=>{
  return {
      id : state.user.id

  }
}

export default withRouter(connect(mapStateToProps,{keepLogin}) (App));
