import React from 'react'
import Cookies from 'universal-cookie';
import { Redirect } from 'react-router-dom'
import {serverUrl} from '../url'
import Moment from 'react-moment'
import Axios from 'axios';
import swal from 'sweetalert';
import DatePicker from "react-date-picker";
import { getPermintaanPinjamanDetailFunction,getDetailDataBorrowerFunction } from './saga';

const kukie = new Cookies()

class Main extends React.Component{
    state = {errorMessage:'',rows:{},items:[],borrowerDetail:{},status:'',endDate:null,diterima:false,ditolak:false,productInfo:''}

    formatMoney=(number)=>
    { return number.toLocaleString('in-RP', {style : 'currency', currency: 'IDR'})}

    componentDidMount(){
        this.getDataDetail()
        this.getDataBorrower()
    }
    componentWillReceiveProps(newProps){
        this.setState({errorMessage:newProps.error})
    }
    getDataDetail =()=>{
       var idLoan = this.props.match.params.idLoan
    this.getDataDetailBtn({idLoan})
    }
    getDataDetailBtn = async function(param){
        const data = await getPermintaanPinjamanDetailFunction (param)
        if(data){
            if(!data.error){
                this.setState({rows:data.dataLender,items:data.dataLender.fees,status:data.dataLender.status})
            }else{
                this.setState({errorMessage:data.error})
            }
        }
    }

    getDataBorrower = async function (){
       
        const param ={id: this.props.match.params.idBorrower}
        const data = await getDetailDataBorrowerFunction(param)

        if (data){
            console.log(data)
            if(!data.error){
                this.setState({borrowerDetail:data.dataBorrower})
            }else{
                this.setState({errorMessage:data.error})
            }
        }        
    }

    handleEndChange = (date)=> {
        this.setState({
          endDate: date
        });
      }
    renderAdminFee = ()=>{
        var jsx = this.state.items.map((val,index)=>{
            return (
            <tr key={index}>
                <td>{val.description}</td>
                <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>
                <td>
                    {String(val.amount).includes("%")?
                     val.amount :
                     this.formatMoney(parseInt(val.amount))}
                </td>
                <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>
                <td>{String(val.amount).includes("%")  ?
                    this.formatMoney(parseInt(val.amount.slice(0,val.amount.indexOf("%")))*this.state.rows.loan_amount/100):
                    this.formatMoney(parseInt(val.amount))}</td>
            </tr>
            )
        })
        return jsx
    }
   
    
    btnTerimaPinjaman = ()=>{
        var idLoan = this.props.match.params.idLoan
        const {endDate} = this.state

        if (endDate === null){
            this.setState({errorMessage:"Tanggal Pencairan Kosong - Harap Cek Ulang"})
        }else{
            var endMonth =''+ (endDate.getMonth()+1),
            endDay = '' + endDate.getDate(),
            endYear = endDate.getFullYear();
            var newDay=''
            if(parseInt(endDay)<10){
                newDay+= "0"+endDay
            }else{
                newDay = endDay
            }
            var newFormatEndDate = endYear+"-"+endMonth+"-"+newDay
            var config = {
                headers: {'Authorization': "Bearer " + kukie.get('tokenClient')}
              };
            
            Axios.get(serverUrl+`lender/loanrequest_list/${idLoan}/detail/approve?disburse_date=${newFormatEndDate}`,config)
            .then((res)=>{
                swal("Permintaan","Diterima","success")
                this.setState({status:"approved",errorMessage:'',diterima:true})
    
            })
            .catch((err)=>console.log(err))
        }
       
    }

    btnTolakPinjaman = ()=>{
        var idLoan = this.props.match.params.idLoan
        var reject = this.refs.alasanPenolakan.value

        if(!reject || reject.trim()===''){
            this.setState({errorMessage:"Alasan Penolakan Kosong - Harap Cek Ulang"})
        }else{
            var config = {
                headers: {'Authorization': "Bearer " + kukie.get('tokenClient')}
              };
            Axios.get(serverUrl+`lender/loanrequest_list/${idLoan}/detail/reject?reason=${reject}`,config)
            .then((res)=>{
                swal("Permintaan","Ditolak","error")
                this.setState({status:"rejected",ditolak:true})
            })
            .catch((err)=>{
                console.log(err.response)
            })
        }
      
    }

    btnBack = ()=>{
        window.history.back()
    }

    getBiayaAdmin =()=>{
        var jsx = this.state.items
        .map((val,index)=>{
            return (
                    <tr key={index}>
                    <td>{val.description}</td>
                    <td>: {this.formatMoney(parseInt(val.amount))}</td>
                    </tr>
            )
        })
         return jsx;
    }
    calculateConvienceFee = (percent)=>{
        var imbalhasil = this.state.rows.interest*this.state.rows.loan_amount/100
        var total = this.state.items.map((val)=>{
            return parseInt(val.amount) * this.state.rows.loan_amount/100
        })
        if(percent) {
            return (parseInt(this.state.rows.total_loan)-parseInt(this.state.rows.loan_amount)-parseInt(imbalhasil) - parseInt(total)) / parseInt(this.state.rows.loan_amount) * 100 
        } else {
            return this.formatMoney(parseInt(this.state.rows.total_loan)-parseInt(this.state.rows.loan_amount)-parseInt(imbalhasil) - parseInt(total))
        }
    }

    render(){
        if(this.state.diterima){
            return (
                <Redirect to='/pinjamansetuju' />
            )   
        }
        
        if(this.state.ditolak){
            return (
                <Redirect to='/pinjamanrejected' />
            )   
        }
        if(kukie.get('token') && kukie.get('tokenClient')){
            return(
                <div>

                    <h2>Pinjaman - Detail</h2>
                    <hr></hr>   
                    <div className="form-group row">
                            <div className="col-12" style={{color:"red",fontSize:"15px",textAlign:'center'}}>
                                    {this.state.errorMessage}
                            </div>
                                
                     </div>
                    {/* -----------------------------------------------------FIRST ROW----------------------------------------------------------------- */}
                    <div className="row" style={{marginTop:"20px", background:"#f7f7f7f7", fontWeight:'bold'}}>
                        <div className="col-12 col-md-6">
                            <table key={this.props.match.params.idLoan}>
                                <tbody>
                                <tr>
                                    <td>ID Pinjaman</td>
                                    <td>: {this.state.rows.id}</td>
                                </tr>
                                <tr>
                                    <td>Nama Nasabah</td>
                                    <td>: {this.state.rows.owner_name}</td>
                                </tr>
                                </tbody>
                             
                            </table>

                        </div>
                        <div className="col-12 col-md-6">
                        <table key={this.props.match.params.idLoan}>
                            
                                <tbody>
                                <tr>
                                    <td>Rekening Peminjam</td>
                                    <td>: {this.state.borrowerDetail.idcard_number}</td>
                                </tr>
                                <tr>
                                    <td>Status Pinjaman</td>
                                    <td>: {
                                    this.state.status === "processing"?
                                        <label style={{color:"blue"}}>Dalam Proses</label>
                                    : this.state.status === "approved"?
                                    <label style={{color:"green"}}>Diterima</label>:
                                    <label style={{color:"red"}}>Ditolak</label>
                                    
                                    }</td>
                                </tr>
                                </tbody>
                               
                            </table>

                        </div>
                        

                    </div>
             {/* -----------------------------------------------------SECOND ROW----------------------------------------------------------------- */}
             <div className="row mt-4" >
                        <div className="col-12 col-md-6">
                            <table>
                                <tbody>
                                <tr>
                                    <td>Pinjaman Pokok</td>
                                    <td>: {this.formatMoney(parseInt(this.state.rows.loan_amount))}</td>
                                </tr>
                                <tr>
                                    <td>Tenor (Bulan)</td>
                                    <td>: {this.state.rows.installment}</td>
                                </tr>
                                <tr>
                                    <td>Total Pinjaman</td>
                                    <td>: {this.formatMoney(parseInt(this.state.rows.total_loan))}</td>
                                </tr>
                                <tr>
                                    <td>Angsuran Perbulan</td>
                                    <td>: {this.formatMoney(parseInt(this.state.rows.layaway_plan))}</td>
                                </tr>
                                </tbody>
                                
                            </table>
                            <table className="mt-2">
                                <thead>
                                <tr >
                                    <th className="text-center" ></th>
                                    <th></th>
                                    <th className="text-center">(%)</th>
                                    <th></th>
                                    <th className="text-center">(Jumlah)</th>
                                </tr>   
                                </thead>
                                <tbody>
                                    <tr>
                                        <td>Imbal Hasil/ Bunga</td>
                                        <td></td>
                                        <td>{this.state.rows.interest}%</td>
                                        <td></td>
                                        <td>{this.state.rows &&  this.state.rows.interest && this.state.rows.loan_amount?this.formatMoney(parseInt(this.state.rows.interest)*parseInt(this.state.rows.loan_amount)/100) :null}</td>
                                    </tr>
                                    {this.renderAdminFee()}
                               
                                </tbody>
                            </table>
                        </div>
                        <div className="col-12 col-md-6">
                            <table>
                                <tbody>
                             
                                <tr>
                                    <td>Tujuan Pinjaman</td>
                                    <td>: {this.state.rows.loan_intention}</td>
                                </tr>
                                <tr>
                                    <td>Detail Tujuan</td>
                                    <td>: {this.state.rows.intention_details}</td>
                                </tr>
                                <tr>
                                    <td>Tanggal Pengajuan</td>
                                    <td>: 
                                    <Moment date={this.state.rows.created_time} format=" DD  MMMM  YYYY" />
                                    </td>
                                </tr>
                                
                                 { this.state.status==='processing'?
                                null:
                                 this.state.status==='approved'?
                                 <tr>
                                    <td>Tanggal Persetujuan</td>
                                    <td>:
                                    <Moment date={this.state.rows.updated_time} format=" DD  MMMM  YYYY" />
                                    </td>
                                 </tr>
                              :
                                <tr>
                                    <td>Tanggal Ditolak</td>
                                    <td>:
                                    <Moment date={this.state.rows.updated_time} format=" DD  MMMM  YYYY" />
                                    </td>
                                 </tr>

                                 }
                                    
                                
                                </tbody>
                                
                            </table>

                        </div>
                        

                 </div>
             {/* -----------------------------------------------------THIRD ROW----------------------------------------------------------------- */}
             <h2 className="mt-4">Info Penghasilan Saat Pengajuan</h2>
            
             <div className="row">
                        <div className="col-12 col-md-12">
                            <table>
                                <tbody>
                                <tr>
                                    <td>Pendapatan perbulan</td>
                                    <td>: {
                                        this.state.borrowerDetail.monthly_income?
                                        this.formatMoney(parseInt(this.state.borrowerDetail.monthly_income))
                                        :0
                                        }</td>
                                </tr>
                                <tr>
                                    <td>Penghasilan lain-lain (jika ada)</td>
                                    <td>: {this.state.borrowerDetail.other_income?this.formatMoney(parseInt(this.state.borrowerDetail.other_income)):0}</td>
                                </tr>
                                <tr>
                                    <td>Sumber Penghasilan lain-lain</td>
                                    <td>: {this.state.borrowerDetail.other_incomesource}</td>
                                </tr>
                                {
                                    this.state.status === "processing" ? 
                                 <tr>
                                 <td>Tanggal Pencairan</td>
                                 <td>:
                                   <DatePicker
                                     format="y-MM-d"
                                     onChange={this.handleEndChange}
                                     value={this.state.endDate}
                                     clearIcon={null}
                                     className="pl-1"
                                 />
                                 </td>
                             </tr>
                                : this.state.status === "approved" ?
                                <tr>
                                <td>Tanggal Pencairan</td>
                                <td>: 
                                <Moment date={this.state.rows.disburse_date} format=" DD  MMMM  YYYY" />                               
                                </td>
                             </tr>: this.state.status === "rejected" ?
                                    <tr>
                                    <td>Alasan Penolakan</td>
                                    <td>: {this.state.rows.reject_reason}  
                                    </td>
                                    </tr>
                            :
                            null
                            }
                            {this.state.status==='processing'?
                             <tr>
                                <td>Alasan Penolakan</td>
                                <td>
                                    : <input type="text" ref="alasanPenolakan" placeholder="Masukan alasan.."/>
                                </td>
                            </tr>
                            :null}
                        
                                </tbody>
                                
                            </table>
                        </div>
            </div>
             {/* -----------------------------------------------------FOURTH ROW----------------------------------------------------------------- */}
             <div className="row mt-3">
                        <div className="col-12 col-md-6">
                            <table>
                                <tbody>
                                    {
                                         this.state.status === "approved" || this.state.status === "rejected"? 
                                         <tr>
                                         <td >
                                             <input type="button" onClick={this.btnBack}  className="btn btn-secondary" value="Kembali"></input>
                                         </td><td></td><td></td>
                            
                                     </tr>
                                         
                                         :
                                         <tr>
                                         <td>
                                             <input type="button" onClick={this.btnTerimaPinjaman} className="btn btn-success" value="Terima"></input>
                                         </td>
                                         <td>
                                             <input type="button" onClick={this.btnTolakPinjaman}  className="btn btn-danger ml-3" value="Tolak"></input>
                                         </td>
                                         <td>
                                             <input type="button" onClick={this.btnBack}  className="btn btn-secondary" value="Kembali"></input>
                                         </td>
                                     </tr>
                                    }
                               
                                </tbody>
                                
                            </table>
                        </div>
            </div>

                </div>
            )
        }
        if(kukie.get('token')){
            return (
                <Redirect to='/login' />
            )    
        }
       
    }
}

export default Main;
