import React from 'react';
import DatePicker from "react-date-picker";
import swal from 'sweetalert'
import { CSVLink } from "react-csv";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Loader from 'react-loader-spinner'
import "react-datepicker/dist/react-datepicker.css";
import {connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import {Link} from 'react-router-dom'
import Pagination from 'rc-pagination';
import './../../support/css/pagination.css'
import localeInfo from 'rc-pagination/lib/locale/id_ID'
import { getPermintaanPinjamanFunction, CSVDownloadFunction, confirmDisburseFunction, changeDisburseDateFunction } from './saga';
import Moment from 'react-moment'
import 'moment/locale/id'
import { getTokenClient, getTokenAuth } from '../index/token';


var arr=[]
class PinjamanSetuju extends React.Component {
  state = {
    rows: [],fromDate:null,toDate:null,
    page: 1,last_page:1,
    rowsPerPage: 10,
    isEdit: false,
    editIndex:Number,
    udahdiklik : false,
    startDate: new Date() ,endDate: new Date(),
    downloadDataCSV: [],downloadModal:false,
    totalData:0,
    loading:true,loadingBtn:false,
    searching:false,errorMessage:'',
    modal:false,idPinjaman:null,ubahTanggalPencairan:new Date(),disburse:false,telahDicairkan:'Dikonfirmasi',statusTanggalDisburse:'Diubah'
  };

  _isMounted =false

  componentDidMount(){
    this._isMounted=true
    this.getAllData()
  }

  componentWillUnmount(){
    this._isMounted=false
  }

  getAllData = async function (){
    const param ={
      status:"approved",
      rows:"10",
      page: this.state.page,
    }
    if(this.state.searching){
      param.start_date = this.state.fromDate
      param.end_date = this.state.toDate
    }


    const data = await getPermintaanPinjamanFunction(param)

    if(data){
      console.log(data)

      if(!data.error){
        this.setState({loading:false,rows:data.loanRequest.data,rowsPerPage:data.loanRequest.rows,page:data.loanRequest.current_page,last_page:data.loanRequest.last_page,totalData:data.loanRequest.total_data})        
      }else{
        this.setState({errorMessage:data.error})
      }
    }
  }

  //UNTUK TANGGAL 
  handleStartChange = (date)=> {
    this.setState({
      startDate: date
    });
  }
  handleEndChange = (date)=> {
    this.setState({
      endDate: date
    });
  }
  
  onBtnSearch = ()=>{
    arr=[]
    this.setState({loading:true,searchModal:true,searching:true})
    const {startDate, endDate} = this.state

      var startMonth =''+ (startDate.getMonth()+1),
      startDay = '' + startDate.getDate(),
      startYear = startDate.getFullYear();

      var endMonth =''+ (endDate.getMonth()+1),
      endDay = '' + endDate.getDate(),
      endYear = endDate.getFullYear();

      if (startMonth.length < 2) startMonth = '0' + startMonth;
      if (startDay.length < 2) startDay = '0' + startDay;

      if (endMonth.length < 2) endMonth = '0' + endMonth;
      if (endDay.length < 2) endDay = '0' + endDay;
      
      var newFormatStartDate = startYear+"-"+startMonth+"-"+startDay+"T00:00:00.000Z"
      var newFormatEndDate = endYear+"-"+endMonth+"-"+endDay+"T23:59:59.000Z"
    
      if(endDate.getTime() < startDate.getTime() ){
        alert("Please input correct month range")
        this.setState({loading:false})
      }
      else{
        this.setState({fromDate:newFormatStartDate,toDate:newFormatEndDate,page:1},()=>{
            this.getAllData()
        })
        
      }
  }

  onBtnReset =()=>{
    this.setState({loading:true,searching:false,page:1},()=>{
      this.getAllData()

    })

  }

//BUAT DOWNLOAD CSV
  

  btnDownloadCsv = ()=>{
    this.setState({loadingBtn:true})
  
    if (arr.length===0){
      swal("Info","Silahkan Pilih Data yang ingin di download","info")
      this.setState({loadingBtn:false})
    }else{
      var id=[]
      for (var j=0;j<arr.length;j++){
        id.push(arr[j])
      }
      const param = {
        id:id.toString()
      }
      this.btnCSVDownload(param)

    }    
  }

  btnCSVDownload = async function (param){
    const data = await CSVDownloadFunction(param)
    if(data){
      if(!data.error){
        this.setState({loadingBtn:false,downloadModal:true,downloadDataCSV:data.data})
      }else{
        this.setState({loadingBtn:false,downloadModal:false,errorMessage:data.error})
      }
    }
  }

  onChangePage = (current) => {
    this.setState({loading:true,page:current},()=>{
        this.getAllData()
    })
  }

  btnKonfirmasi = (id)=>{
      this.confirmDisburse({id})
  } 

  confirmDisburse = async function (param){
    const data = await confirmDisburseFunction (param)

    if(data){
      if(!data.error){
          this.setState({telahDicairkan:'Dikonfirmasi'}, () => { this.getAllData()})
          swal("Success","Dana telah dicairkan","success")
      }else{
          swal("Fail","Konfirmasi Gagal","error")
      }
    }
  }

  handleTanggalPencairan =(e)=>{
    this.setState({ubahTanggalPencairan:e})
  }
  
  handleCheckBox =(e)=>{
    if(e.target.checked){
      arr.push(parseInt(e.target.value))
    }else{
      arr.splice(arr.indexOf(parseInt(e.target.value)),1)
    }
    
  }
  ubahTanggalPencairanBtn = ()=>{
    this.setState({disburse:true})
    const {ubahTanggalPencairan,idPinjaman} = this.state

    let startMonth =''+ (ubahTanggalPencairan.getMonth()+1),
    startDay = '' + ubahTanggalPencairan.getDate(),
    startYear = ubahTanggalPencairan.getFullYear();

    if (startMonth.length < 2) startMonth = '0' + startMonth;
    if (startDay.length < 2) startDay = '0' + startDay;

    const changeDate = startYear+"-"+startMonth+"-"+startDay
    const param ={
      id:idPinjaman,
      date:changeDate
    }
    
    this.disburseDate(param)
  }

  disburseDate = async function (param){
    const data = await changeDisburseDateFunction(param)

    if(data){
      if(!data.error){
        swal("Success","Tanggal pencairan berhasil diubah","success")
        this.setState({modal:false,disburse:false,statusTanggalDisburse:'Diubah'},()=>{
          this.getAllData()
        })
      }else{
        swal("Fail","Tanggal pencairan gagal diubah","error")
        this.setState({disburse:false})

      }
    }
  } 
  renderJSX = ()=>{
    if (this.state.loading){
      return  (
        <tr>
          <td align="center" colSpan={12}>
                <Loader 
            type="Circles"
            color="#00BFFF"
            height="40"	
            width="40"
        />   
          </td>
        </tr>
      )
      
      
   
  }else{
    if (this.state.rows.length===0){
      return(
        <tr>
          <td align="center" colSpan={12}>Data Empty</td>
        </tr>
      )
    }
    var jsx = this.state.rows.map((val,index)=>{
      return (
          <tr key={index}>
          <td align="center">
          <input type="checkbox" onClick={this.handleCheckBox} defaultChecked={arr.includes(val.id) ?true:false} value={val.id} style={{width:"20px",height:"20px"}} /> 
          </td>
          {/* <td align="center">{index+1}</td> */}
          <td align="center">{this.state.page >0 ? index+1 + (this.state.rowsPerPage*(this.state.page -1)) : index+1}</td>
          <td align="center">{val.id}</td>
          <td align="center">{val.owner_name}</td>
          <td align="center">{val.service.toString()}</td>
          <td align="center">{val.product.toString()}</td>
          <td align="center">
          <Moment locale="id" date={val.created_time} format=" DD  MMMM  YYYY" /> 
          </td>
          <td align="center">
          <Moment locale="id" date={val.approval_date} format=" DD  MMMM  YYYY" /> 
          </td>
          <td align="center">
          <Moment locale="id" date={val.disburse_date} format=" DD  MMMM  YYYY" /> 
          </td>
          <td align="center">
          {val.disburse_status === 'processing' ?
            <input type="button" className="btn btn-primary" value="Konfirmasi" onClick={()=>this.btnKonfirmasi(val.id)}></input>
          : this.state.telahDicairkan}
          </td>

          <td align="center">
            { 
              val.disburse_date_changed === false && val.disburse_status ==='processing'? 
              <i className="fas fa-edit" style={{color:"black",fontSize:"28px",cursor:"pointer"}} onClick={()=>this.setState({modal:true,idPinjaman:val.id})}></i>
            :val.disburse_date_changed?
            this.state.statusTanggalDisburse: "-"
            }
             
          </td>
          <td align="center">
              <Link style={{textDecoration:"none"}} to={`/permintaanpinjamanDetail/${val.id}`}>
                  <i className="fas fa-eye"  style={{color:"black",fontSize:"28px"}}></i>
              </Link>
          </td>
      </tr>
      )
  })
   return jsx;

  }
    
  }
  //FUNCTION BUAT MODAL
 
  btnCancelModalDownload = ()=>{
    this.setState({downloadModal:false,downloadDataCSV:[],searchModal:false})
    var checkboxes = document.querySelectorAll('input[type=checkbox]:checked')
    for (var i = 0; i < checkboxes.length; i++) {
          checkboxes[i].checked = false;
  }
    arr = []
  }
  renderBtnOrLoading =()=>{
    if (this.state.loadingBtn){
        return  <Loader 
        type="ThreeDots"
        color="#00BFFF"
        height="30"	
        width="30"
        
     />   
    }
    else{
        return(
          <input type="button" className="btn btn-primary ml-3" onClick={this.btnDownloadCsv} value="CSV Download"></input>
        )
    }
}
    renderBtnDisburseDate =()=>{
      if(this.state.disburse){
        return(
        <Button color="primary" onClick={this.ubahTanggalPencairanBtn}>   
            <Loader 
             type="ThreeDots"
             color="#00BFFF"
             height="30"	
             width="30"
         />       
        </Button>  
        )
      }else{
        return(
          <Button color="primary" onClick={this.ubahTanggalPencairanBtn}>Simpan</Button>    

        )
      }
    }

    
  render() {
    if(getTokenClient() && getTokenAuth()){
    return (
        <div className="container">

        <Modal isOpen={this.state.downloadModal} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Jumlah Download CSV: {arr.length} item(s)</ModalHeader>
          <ModalBody>
          <CSVLink data={this.state.downloadDataCSV} 
          > Click Here to Download CSV </CSVLink>
          </ModalBody>
          <ModalFooter>
            <Button color="secondary" onClick={this.btnCancelModalDownload}>Close</Button>
          </ModalFooter>
        </Modal>
      
        <Modal isOpen={this.state.modal} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Ubah Tanggal Pencairan</ModalHeader>
          <ModalBody>

            <DatePicker
            className="ml-3"
            format="yyyy-MM-dd"
            onChange={this.handleTanggalPencairan}
            value={this.state.ubahTanggalPencairan}
            clearIcon={null}
          /> 
            
                  
            
          </ModalBody>
          <ModalFooter>

            {this.renderBtnDisburseDate()}
            <Button color="secondary" onClick={()=>this.setState({modal:false})}>Tutup</Button>
          </ModalFooter>
        </Modal>


        <nav className="navbar">
          <h2>Pinjaman Disetujui</h2>
       
     
        </nav>
         <div className="row">
            <div className="col-12 col-md-12">
                
        <DatePicker
        className="ml-3"
        format="yyyy-MM-dd"
        onChange={this.handleStartChange}
        value={this.state.startDate}
        clearIcon={null}
            />  -  <DatePicker
        format="y-MM-d"
        onChange={this.handleEndChange}
        value={this.state.endDate}
        clearIcon={null}
        
        />
            <div style={{float:"right"}}>
                <input type="button" className="btn btn-primary ml-2" onClick={this.onBtnSearch} value="Search"></input>
                <input type="button" className="btn btn-primary ml-2" onClick={this.onBtnReset} value="Reset"></input>
            </div>    
            </div>
        </div> 
        <hr></hr>
          <table className="table table-hover">
          <thead className="table-warning">
              <tr>
                <th className="text-center" scope="col"><i className="far fa-check-square"></i></th>
                <th className="text-center" scope="col">#</th>
                <th className="text-center" scope="col">Id Pinjaman</th>
                <th className="text-center" scope="col">Nama Nasabah</th>
                <th className="text-center" scope="col">Layanan</th>
                <th className="text-center" scope="col">Produk</th>
                <th className="text-center" scope="col">Tanggal Pengajuan</th>
                <th className="text-center" scope="col">Tanggal Approval</th>
                <th className="text-center" scope="col">Tanggal Pencairan</th>
                <th className="text-center" scope="col">Telah Dicairkan</th>
                <th className="text-center" scope="col">Ubah Tanggal Pencairan</th>
                <th className="text-center" scope="col">Detail</th>
              </tr>     
          </thead>
            <tbody>
            {this.renderJSX()}
            </tbody>
            <tfoot>
              <tr>
              </tr>
            </tfoot>
          </table>
          <hr></hr>
          {this.renderBtnOrLoading()}        
          <nav className="navbar" style={{float:"right"}}> 
          <Pagination className="ant-pagination"  
                current={this.state.page}
                showTotal={(total, range) => `${range[0]} - ${range[1]} of ${total} items`}
                total={this.state.totalData}
                pageSize={this.state.rowsPerPage}
                onChange={this.onChangePage}
                locale={localeInfo}
                />
          </nav>
        
        </div>
    );
}
else if (getTokenAuth()){
  return  <Redirect to='/login' />
}
  }
}

const mapStateToProp = (state)=>{
  return{
      role: state.user.role,
      id: state.user.id
  }
}
export default connect (mapStateToProp)(PinjamanSetuju) ;
