import React from 'react'
import Logo from './../../support/img/logo.jpeg'
import './../../support/css/login.css'
import Loader from 'react-loader-spinner'
import swal from 'sweetalert'
import {Redirect} from 'react-router-dom'
import {keepLogin} from '../../1.actions'
import {connect} from 'react-redux'
import { postLoginBankDashboardFunction, getTokenBorrowerFunction, getUserProfileFunction } from './saga'
import { setProfileUser } from './token'
import { Grid, Button, TextField } from '@material-ui/core'


 
class Login extends React.Component{
    _isMounted = false;

    state={
        loading:false, 
        isLogin:false,
        username: '',
        password: '', 
    }

    componentDidMount(){
        this._isMounted = true;
    }

    componentWillUnmount() {
        this._isMounted = false;     
    }
  
    //LOGIN BUTTON
    btnLogin = ()=>{
        this.setState({loading:true})

        var username=this.refs.username.value
        var password=this.refs.password.value

        const param = {
            key: username,
            password
        }

        if (username==="" || password===""){
            swal("Error","Username and Password Empty","error")
            this.setState({loading:false})

        }else{
            this.postLogin(param)
        }
      
    } 
    
    postLogin = async function (param) {
        const data = await postLoginBankDashboardFunction(param, getTokenBorrowerFunction, getUserProfileFunction)

            if(data) {
                if(!data.error) {
                    let userPermission = data.dataPermission || {};  
                
                    setProfileUser(JSON.stringify(userPermission))

                    this.setState({loading:false , isLogin : true})
                } else {
                    this.setState({loading:false})
                    swal("Gagal Login","Cek ulang Username dan Password","info")
                }
            }
    }

    renderBtnOrLoading =()=>{
        if (this.state.loading){
            return (
                <Loader 
                    type="Circles"
                    color="#00BFFF"
                    height="10"	
                    width="10"
                />   
            )
        }
        else{
            return(
                <input type="button" className="loginBtn" onClick={this.btnLogin} style={{marginTop:"20px"}} value="Sign in"/> 
            )
        }
    }

    handleChangeTextField = (e) => {
        console.log(e.target.id)
        const label = e.target.id;

        this.setState({
            [label] : e.target.value,
        })
    }

    render(){
        if(this.state.isLogin){
            return(
                <Redirect to='/' />
            )
        }
        // alert('masuk')
       

            return (
                <Grid container>
                    <Grid item xs={12} sm={4}>
                        
                    </Grid>
                    <Grid item xs={12} sm={4}>
                        <Grid container className='loginContainer'>
                            <Grid item xs={12} sm={12} style={{textAlign:'center'}}>
                                <img src={require('./../../icons/LogoAsira.png')} alt='' style={{width:'60%'}} />
                            </Grid>
                            <Grid item xs={12} sm={12}  className='loginBox'>
                                <Grid container>
                                    <Grid item xs={12} sm={12} style={{marginTop:20}}>
                                        <h5>Bank Dashboard</h5> 
                                    </Grid>
                                    <Grid item xs={12} sm={12} style={{marginTop:20}}>
                                        <TextField
                                            id={'username'}
                                            size="small"
                                            margin="dense"
                                            variant='outlined'
                                            style={{width:"70%"}}
                                            label={'Username'}
                                            value={this.state.username}
                                            onChange={this.handleChangeTextField}
                                        /> 
                                    </Grid>
                                    <Grid item xs={12} sm={12} style={{marginTop:10}}>
                                        <TextField
                                            id={'password'}
                                            type='password'
                                            size="small"
                                            margin="dense"
                                            variant='outlined'
                                            style={{width:"70%"}}
                                            label={'Password'}
                                            value={this.state.password}
                                            onChange={this.handleChangeTextField}
                                        />  
                                        
                                    </Grid>
                                    <Grid item xs={12} sm={12} style={{marginTop:20}}>
                                        <Button
                                            variant='contained'
                                            style={{backgroundColor: '#20B889', width:'70%', color:'white'}}
                                            onClick={this.btnLogin}
                                        >
                                            {'Masuk'}
                                        </Button>
                                    </Grid>
                                    <Grid item xs={12} sm={12} style={{marginTop:20, marginBottom:20}}>
                                        <img src={require('./../../icons/powered.svg')} alt='' style={{width:'30%'}} />
                                    </Grid>
                                </Grid>
                                
                            </Grid>
                        </Grid>
                        
                    </Grid>
                    <Grid item xs={12} sm={4}>
                        
                    </Grid>
                </Grid>
                // <div className="App loginContainer mr-3">
                //     <div className="row mt-2">
                //         <div className="col-6 col-md-6 mt-3">
                //            <h5>Bank Dashboard</h5> 
                //         </div>
                //         <div className="col-6 col-md-6 mt-2">
                //             <img src={Logo} alt="logo" width="60%"></img>
                //         </div>
                //     </div>
    
                //     <div className="row">
                //         <div className="col-12 col-md-12">
                //             <hr/>
                //             <h2>Sign In</h2>
                //             <input type="text" placeholder="Username" ref="username" className="form-control"/>
                //             <input type="password" placeholder="Password" ref="password" className="form-control"/>
                //             {this.renderBtnOrLoading()}
                //             <p style={{marginTop:"5px"}}>Forgot your password?</p>
                       
                //         </div>
                //     </div>
                // </div>
            )

        }
      
       
    
}

export default connect(null,{keepLogin}) (Login);
