import React from 'react';
import axios from 'axios'
import swal from 'sweetalert'
import {connect } from 'react-redux'
import Cookie from 'universal-cookie'
import { Redirect } from 'react-router-dom'
import {serverUrl} from './url'
import {Link} from 'react-router-dom'
import Loader from 'react-loader-spinner'
import QueryString from 'query-string'


const kukie = new Cookie()


class PinjamanRejected extends React.Component {
  state = {
    rows: [],detailNasabah:{}, searchRows:null,
    page: 1,
    rowsPerPage: 10,
    isEdit: false,
    editIndex:Number,
    udahdiklik : false,
    totalData:0,
    last_page:1,
    loading:true
  };

  //-----------------------------------NIKO FUNCTION-------------------------------------------------------------
  
  componentDidMount(){
    this.getAllData()
  }

  getAllData = ()=>{
      var config = {
        headers: {'Authorization': "Bearer " + kukie.get('tokenClient')}
      };
      var newLink =`lender/loanrequest_list`
      if (this.props.location.search){
        var hasil = this.getLink()
        if(!isNaN(hasil)){
          newLink += `?id=${hasil}&status=rejected`
        }else{
          newLink += `?owner_name=${hasil}&status=rejected`
        }
      }else{
        newLink += `?status=rejected`
      }
      axios.get(serverUrl+newLink,config)
      .then((res)=>{
          console.log(res.data)
          this.setState({loading:false,rows:res.data.data,rowsPerPage:res.data.rows,totalData:res.data.total_data,last_page:res.data.last_page,page:res.data.current_page})

      })
      .catch((err)=>{
          console.log(err)
      })

  }
  getLink = ()=>{
    var obj = QueryString.parse(this.props.location.search)
   return obj.query 
  }
  pushUrl = ()=>{
    var newLink ='/pinjamanrejected/search'
    var params =[]
    //categoryDropdown,search
    if(this.refs.search.value){
        params.push({
            params:'query',
            value:this.refs.search.value
        })
    }
    

    for (var i=0;i<params.length;i++){
        if(i===0){
            newLink += '?'+params[i].params+ '='+ params[i].value
        }else{
            newLink += '&'+params[i].params+ '='+ params[i].value
        }
    }
    this.props.history.push(newLink)
}

  

  onBtnSearch = ()=>{
    this.pushUrl()
    var searching = this.refs.search.value
    this.setState({loading:true,searchRows:searching})
    var config = {
      headers: {'Authorization': "Bearer " + kukie.get('tokenClient')}
    };
    var newLink =`lender/loanrequest_list?`
    if(searching){
      //search function
      if(!isNaN(searching)){
        newLink += `id=${searching}&status=rejected`
      }else{
        newLink += `owner_name=${searching}&status=rejected`
      }
      axios.get(serverUrl+newLink,config)
      .then((res)=>{
          console.log(res.data)
          this.setState({loading:false,rows:res.data.data})
      })
      .catch((err)=>{
          console.log(err)
      })
    }else{
      axios.get(serverUrl+newLink+`status=rejected`,config)
      .then((res)=>{
        
          this.setState({loading:false,rows:res.data.data,searchRows:null})
      })
      .catch((err)=>{
          console.log(err)
      })
    }
  }

