import React from 'react';
import DatePicker from "react-date-picker";
import swal from 'sweetalert'
import { CSVLink } from "react-csv";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Loader from 'react-loader-spinner'
import "react-datepicker/dist/react-datepicker.css";
import {connect } from 'react-redux'
import Cookie from 'universal-cookie'
import { Redirect } from 'react-router-dom'
import {Link} from 'react-router-dom'
import Pagination from 'rc-pagination';
import './../../support/css/pagination.css'
import localeInfo from 'rc-pagination/lib/locale/id_ID'
import { getPermintaanPinjamanFunction, CSVDownloadFunction } from './saga';

const kukie = new Cookie()

var arr=[]
class PinjamanSetuju extends React.Component {
  state = {
    rows: [],fromDate:null,toDate:null,
    page: 1,last_page:1,
    rowsPerPage: 10,
    isEdit: false,
    editIndex:Number,
    udahdiklik : false,
    startDate: new Date() ,endDate: new Date(),
    downloadDataCSV: [],downloadModal:false,
    totalData:0,
    loading:true,loadingBtn:false,
    halamanConfig:`orderby=id&sort=DESC&status=approved&rows=10`,searching:false,errorMessage:''
  };

  //-----------------------------------NIKO FUNCTION-------------------------------------------------------------
  _isMounted =false
  componentDidMount(){
    this._isMounted=true
    this.getAllData()
  }
  componentWillUnmount(){
    this._isMounted=false
