import React from 'react';
import './App.css';
import {Route,withRouter,Switch} from 'react-router-dom'
import {connect} from 'react-redux'
import {keepLogin} from './1.actions'
import {serverUrl} from './components/url'

import PageNotFound from './components/404'
import Login from './components/index/login'
import Home from './components/index/main'
import Nasabah from './components/profileNasabah/profileNasabah'
import ScrollTop from './components/scrollToTop'
import Header from './components/index/header'
import Testing from './components/testing'
import profileNasabahDetail from './components/profileNasabah/profileNasabahDetail'
import PermintaanPinjaman from './components/permintaanPinjaman/permintaanPinjaman'
import PermintaanPinjamanDetail from './components/permintaanPinjaman/permintaanPinjamanDetail'
import PinjamanSetuju from './components/permintaanPinjaman/pinjamanSetuju'
import PinjamanRejected from './components/permintaanPinjaman/pinjamanRejected'
import PencairanList from './components/pencairan/pencairanList'
import PencairanDetail from './components/pencairan/pencairanDetail'
import CalonNasabahList from './components/calonNasabah/calonNasabahList';
import calonNasabahDetail from './components/calonNasabah/calonNasabahDetail';
import axios from 'axios'
import { getTokenClient, setTokenAuth, getProfileUser, getTokenAuth} from './components/index/token';
import { checkPermission } from './components/global/globalFunction';
import { Grid } from '@material-ui/core';
import Drawer from './components/subComponent/Drawer';

class App extends React.Component {
  state ={loading : true , tokenClient : null , Token : null}

  componentDidMount(){
    this.getAuth()
  }

  componentWillReceiveProps(nextProps) {
    console.log(nextProps.location.pathname)
    console.log(this.props.location.pathname)
    console.log(nextProps)
    console.log(this.props)
    if(nextProps.location.pathname === '/' && !getTokenAuth()) {
      this.getAuth()
    }
    
  }


  getAuth = ()=>{
    var url =serverUrl+"clientauth"
    axios.get(url ,{
      auth : {
        username : 'reactkey',
        password : 'reactsecret'
      }
    }).then((res)=>{
      setTokenAuth(res.data.token)
      this.setState({loading : false})
    }).catch((err)=>{
      console.log(err.toString())
      setTimeout(function(){ alert("Coba reload halaman/ cek koneksi internet"); }, 5000);
    })
  }


  render() {
    if(this.state.loading){
      return(
        <p> loading ....</p>
      )
    }
    
      return (
      
        <div>
          <ScrollTop>
            <Grid container>
              
              {
                getTokenClient() && getProfileUser() ? 
                <Grid item xs={12} sm={12}>
                  {/* <Header/> */}
                  <Drawer/>
                </Grid>
                :
                <Login />
              }
              
              
            </Grid>
          </ScrollTop>
        </div>
     
      );
    
  
     
 
  }
}
  
const mapStateToProps = (state)=>{
  return {
      id : state.user.id

  }
}

export default withRouter(connect(mapStateToProps,{keepLogin}) (App));
