import React from 'react'
import Cookies from 'universal-cookie';
import { Redirect } from 'react-router-dom'
import Loader from 'react-loader-spinner'
import {Link} from 'react-router-dom'
// import SubTable from './../subComponent/SubTable'
import localeInfo from 'rc-pagination/lib/locale/id_ID'
import Pagination from 'rc-pagination';
import { getPermintaanPinjamanFunction } from '../permintaanPinjaman/saga';


const cookie = new Cookies()

// const columnDataRole = [
//     {
//         id: 'id',
//         numeric: 'true',
//         disablePadding: false,
//         label: 'Id Role Permission',
//     },
//     {
//         id: 'name',
//         numeric: 'false',
//         disablePadding: true,
//         label: 'Nama Role',
//     },
//     { id: 'status', numeric: 'false', disablePadding: true, label: 'Status' },
//     {
//         id: 'updated_time',
//         numeric: 'false',
//         disablePadding: false,
//         label: 'Update Date',
//         time: 'true',
//     },
// ]

class PencairanList extends React.Component{
    _isMounted = false;

    state = {
        loading:true, 
        listPencairan: [],
        page: 1,
        rowsPerPage: 10,
        search: '',
    }

    componentDidMount(){
        this._isMounted = true;
        this.refresh()
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    refresh = async function(){
        const param = {
            disburse_status: true,
            status: 'approved',
        };

        const search = this.state.search;
        param.rowsPerPage = this.state.rowsPerPage;
        param.page = this.state.page;

        if(search.toString().trim().length !== 0) {
            if(!isNaN(search)) {
                param.id = parseInt(search)
            } else {
                param.nama_nasabah = search;
            }
        }

        const data = await getPermintaanPinjamanFunction(param);

        if(data) {
            console.log(data)
            if(!data.error) {
                this.setState({
                    listPencairan: data.loanRequest,
                    totalData: data.totalData,
                    loading: false,
                })
            } else {
                this.setState({
                    errorMessage: data.error,
                    loading: false,
                })
            }      
        }
    }

    renderJSX = () => {

        if (this.state.loading){
            return  (
              <tr  key="loader">
                <td align="center" colSpan={6}>
                    <Loader 
                        type="Circles"
                        color="#00BFFF"
                        height="40"	
                        width="40"
                    />   
                </td>
              </tr>
            )
        } else if(!this.state.listPencairan || (this.state.listPencairan && this.state.listPencairan.length === 0)) {
            <tr key='noData'>
                <td align="center"> No Data </td>
            </tr>
        } 
        
        
        var jsx = this.state.listPencairan && this.state.listPencairan.map((val,index)=>{
            return(
                <tr key={index}>
                    <td align="center">{this.state.page >1 ? (index+1 + this.state.rowsPerPage*(this.state.page-1)) : index+1}</td>
                    <td align="center">{val.id}</td>
                    <td align="center">{val.name}</td>
                    <td align="center">{val.datePencairan}</td>
                    <td align="center">{val.status}</td>
                    <td align="center">
                        <Link to={`/detailPencairan/${val.id}`} className="mr-2">
                            <i className="fas fa-edit" style={{color:"black",fontSize:"18px"}}/>
                        </Link>
                        {/* <Link to={`/detailRolePermission/${val.id}`} >
                            <i className="fas fa-eye" style={{color:"black",fontSize:"18px"}}/>
                        </Link> */}
                    </td>
                </tr>  
            )
                   
        })
                     
        return jsx;

    }

    onChangePage = (current, pageSize) => {
        this.setState({
            loading:true,
            page:current,
        }, () => {
            this.refresh();
        })
    }
    
    changeSearch = (e) => {
        this.setState({
            loading:true,
            search: e.target.value,
        }, () => {
            this.refresh();
        })
    }

    render(){
        
        
        if(cookie.get('tokenClient')){
            return(
                <div className="container">
                    <div className="row">
                        <div className="col-7">
                             <h2 className="mt-3">Pencairan - Loan Telah Dicairkan</h2>
                        </div>
                        <div className="col-8" style={{color:"red",fontSize:"15px",textAlign:'left'}}>
                            {this.state.errorMessage}
                        </div>  
                        <div className="col-4">
                            <div className="input-group">
                                <input type="text" className="form-control" placeholder="Search" value={this.state.search} onChange={this.changeSearch} style={{width:"150px"}} />
                                <span className="input-group-addon ml-2" style={{border:"1px solid grey",width:"35px",height:"35px",paddingTop:"2px",borderRadius:"4px",paddingLeft:"2px",marginTop:"6px",cursor:"pointer"}} onClick={this.onBtnSearch}> 
                                <i className="fas fa-search" style={{fontSize:"28px"}} ></i></span>
                            </div>
                        </div> 
                    </div>
                   <hr/>

                   {/* <SubTable
                        // headerTitle={'List Application'}
                        // search={this.state.flagAdd ? searchBar : null}
                        columnData={columnDataRole}
                        data={this.state.listPencairan}
                        idKey={'id'}
                        // onAddFunction={
                        //     // this.state.flagAdd ? this.handleAddApplication : null
                        // }
                        // onRowClickedFunction={this.handleViewApplication}
                    /> */}
                   <table className="table table-hover">
                   <thead className="table-warning">
                        <tr >
                            <th className="text-center" scope="col">#</th>
                            <th className="text-center" scope="col">ID Pinjaman</th>
                            <th className="text-center" scope="col">Nama Nasabah</th>
                            <th className="text-center" scope="col">Tanggal Pencairan</th>
                            <th className="text-center" scope="col">Status</th>
                            <th className="text-center" scope="col">Action</th>
                        </tr>     
                    </thead>
                       <tbody>
                          {this.renderJSX()}
                       </tbody>
                   </table>
                   <hr></hr>
                    <nav className="navbar" style={{float:"right"}}> 

                        <Pagination className="ant-pagination"  
                            showTotal={(total, range) => `${range[0]} - ${range[1]} of ${total} items`}
                            total={this.state.totalData}
                            pageSize={this.state.rowsPerPage}
                            onChange={this.onChangePage}
                            locale={localeInfo}
                        />     
                    </nav>
                </div>
            )
        }
        if(!cookie.get('tokenClient')){
            return (
                <Redirect to='/login' />
            )    
        }
       
    }
}

export default PencairanList;
