import React from 'react'
import { Redirect } from 'react-router-dom'
// import CheckBox from '../subComponent/CheckBox';
import Grid from '@material-ui/core/Grid'
import Loader from 'react-loader-spinner'
import { connect } from 'react-redux';
import { withRouter } from 'react-router';
import { withStyles } from '@material-ui/styles';
import { compose } from 'redux';
import { getPermintaanPinjamanDetailFunction } from '../permintaanPinjaman/saga';
import { formatNumber, handleFormatDate } from '../global/globalFunction';
import { getTokenClient } from '../index/token';
import LineGmr from './../../support/img/line.jpeg'
import GridDetail from '../subComponent/GridDetail';

const styles = (theme) => ({
  container: {
    flexGrow: 1,
  },
});

class PencairanDetail extends React.Component{
    _isMounted = false;
    
    state = {
      diKlik:false,
      errorMessage:'',
      dataDetail: {},
      loanId: 0,
      disabled: true,
      loading: true,
    };

    componentDidMount(){
      this._isMounted = true;

      this.setState({
        loanId: this.props.match.params.idLoan,
      },() => {
        this.refresh();
      })
      
    }

    componentWillUnmount() {
      this._isMounted = false;
    }

    refresh = async function(){
      const param = {
        idLoan: this.state.loanId,
      };

      const data = await getPermintaanPinjamanDetailFunction(param, null);

      if(data) {    
          if(!data.error) {
            if(data.dataLender && data.dataLender.status && data.dataLender.disburse_status && data.dataLender.status.toString().toLowerCase() === 'approved' && data.dataLender.disburse_status.toString().toLowerCase() === 'confirmed') {
              this.setState({
                dataDetail: data.dataLender,
                dataBorrower: data.dataLender && data.dataLender.borrower_info,
                loading: false,
              })
            } else {
              this.setState({diKlik: true})
            }          
          } else {
            this.setState({
              errorMessage: data.error,
              loading: false,
            })
          }      
      }
    }

    btnCancel = ()=>{
      this.setState({diKlik:true})
    }
    componentWillReceiveProps(newProps){
      this.setState({errorMessage:newProps.error})
    }

    findAmount = (dataFees, stringFee, amountPinjamanPokok, charPersen) => {
      let feeNew = '';

      if(dataFees && stringFee && amountPinjamanPokok) {
        for(const key in dataFees) {
          if(dataFees[key].description.toString().toLowerCase() === stringFee.toString().toLowerCase()) {
            if(dataFees[key].amount.toString().toLowerCase().includes('%')) {
              if(charPersen) {
                feeNew = dataFees[key].amount;
              } else {
                feeNew = parseInt(dataFees[key].amount) * amountPinjamanPokok / 100;
              }
            } else {
              if(charPersen) {
                feeNew = parseInt(dataFees[key].amount)  * 100 / amountPinjamanPokok;
              } else {
                feeNew = dataFees[key].amount;
              }
            }
          }
        }
      }

      return parseInt(feeNew);
    }

    render(){
        if(this.state.diKlik){
            return <Redirect to='/pencairanList'/>            
        } else if (this.state.loading){
          return  (
            <div  key="zz">
              <div align="center" colSpan={6}>
                <Loader 
                  type="Circles"
                  color="#00BFFF"
                  height="40"	
                  width="40"
                />   
              </div>
            </div>
          )
        } else if(getTokenClient()){
            return(
                <div className="container mt-4">
                  <h3 style={{color:"#2076B8"}}>Loan Telah Dicairkan</h3>
                  
                  <Grid container spacing={0} style={{paddingLeft: '2vh', marginBottom:'2vh', color:"red",fontSize:"15px",textAlign:'left'}}>
                    {this.state.errorMessage}
                  </Grid>  

                  {/* Title Section */}
                        <GridDetail
                              gridLabel={[6,6]}
                              background
                              label={[
                                  ['ID Pinjaman','Nama Nasabah'],
                                  ['Rekening Pinjaman','Status Pinjaman'],
                              ]}
                              data={this.state.dataDetail &&this.state.dataBorrower && [
                                  [
                                  this.state.dataDetail.id,
                                  this.state.dataDetail.borrower_name
                                  ],
                                  [
                                  this.state.dataBorrower.bank_accountnumber,
                                  this.state.dataDetail.status && this.state.dataDetail.status.toString().toLowerCase() === 'approved' && this.state.dataDetail.disburse_status && this.state.dataDetail.disburse_status.toString().toLowerCase() === 'confirmed' ? {value:"Telah Dicairkan", color:'blue'}  : ''
                                  ]
                                  
                              ]}                 
                        />

                  {/* Detail Section */}
                  
                  <img src={LineGmr} alt="blue line" />
                  <GridDetail
                           gridLabel={[6,6]}
                           label={[
                               ['Pinjaman Pokok','Tenor (Bulan)','Total Pinjaman','Angsuran Perbulan'],
                               ['Tujuan Pinjaman','Detail Tujuan','Tanggal Pengajuan','Tanggal Persetujuan' ],
                           ]}
                           data={this.state.dataDetail && [
                               [
                                  "Rp. "+ formatNumber(parseInt(this.state.dataDetail.loan_amount)),
                                   this.state.dataDetail.installment,
                                   "Rp. "+formatNumber(parseInt(this.state.dataDetail.total_loan)),
                                   "Rp. "+formatNumber(parseInt(this.state.dataDetail.layaway_plan)),
                               ],
                               [
                                this.state.dataDetail.loan_intention,
                                this.state.dataDetail.intention_details,
                                handleFormatDate(this.state.dataDetail.created_time),
                                handleFormatDate(this.state.dataDetail.updated_time),
                               ],
                           ]}                 
                     />

                 

                  {/* Fee Section */}
                  <img src={LineGmr} alt="blue line" />
                
                  <Grid container spacing={0} style={{paddingLeft: '2vh', marginBottom:'2vh'}}>

                    <Grid item sm={6} xs={12}>
                      <Grid container spacing={0}>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={4} xs={4}>
                              
                            </Grid>
                            <Grid item sm={4} xs={4} style={{textAlign: 'center'}}>
                              <b> (%) </b>
                            </Grid>
                            <Grid item sm={4} xs={4} style={{textAlign: 'center'}}>
                              <b> (Jumlah) </b>
                            </Grid>
                          </Grid>
                        </Grid>
                      
                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={4} xs={4}>
                              Imbal Hasil/ Bunga
                            </Grid>
                            <Grid item sm={4} xs={4} style={{textAlign: 'center'}}>
                              {this.state.dataDetail && this.state.dataDetail.interest} %
                            </Grid>
                            <Grid item sm={1} xs={1} style={{textAlign: 'right'}}>
                              Rp 
                            </Grid>
                            <Grid item sm={3} xs={3} style={{textAlign: 'right'}}>
                              {formatNumber(this.state.dataDetail && this.state.dataDetail.loan_amount && this.state.dataDetail.interest && parseInt(this.state.dataDetail.interest * this.state.dataDetail.loan_amount / 100), true)}
                            </Grid>
                          </Grid>
                        </Grid>
                      
                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={4} xs={4}>
                              Admin Fee
                            </Grid>
                            <Grid item sm={4} xs={4} style={{textAlign: 'center'}}>
                              {this.findAmount(this.state.dataDetail && this.state.dataDetail.fees, 'Admin Fee',this.state.dataDetail && this.state.dataDetail.loan_amount,true)} %
                            </Grid>
                            <Grid item sm={1} xs={1} style={{textAlign: 'right'}}>
                              Rp 
                            </Grid>
                            <Grid item sm={3} xs={3} style={{textAlign: 'right'}}>
                              {formatNumber(this.findAmount(this.state.dataDetail && this.state.dataDetail.fees, 'Admin Fee',this.state.dataDetail && this.state.dataDetail.loan_amount,false), true)}
                            </Grid>
                          </Grid>
                        </Grid>
                      
                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={4} xs={4}>
                              Convenience Fee
                            </Grid>
                            <Grid item sm={4} xs={4} style={{textAlign: 'center'}}>
                              {this.findAmount(this.state.dataDetail && this.state.dataDetail.fees, 'Convenience Fee',this.state.dataDetail && this.state.dataDetail.loan_amount,true)} %
                            </Grid>
                            <Grid item sm={1} xs={1} style={{textAlign: 'right'}}>
                              Rp 
                            </Grid>
                            <Grid item sm={3} xs={3} style={{textAlign: 'right'}}>
                              {formatNumber(this.findAmount(this.state.dataDetail && this.state.dataDetail.fees, 'Convenience Fee',this.state.dataDetail && this.state.dataDetail.loan_amount,false), true)}
                            </Grid>
                          </Grid>
                        </Grid>
                      
                      </Grid>
                    </Grid>

                    <Grid item sm={6} xs={12}>
                    </Grid>            

                  </Grid>
                 
                  {/* Info Penghasilan Section */}
                  <Grid container spacing={0} style={{paddingLeft: '2vh', marginBottom:'2vh'}}>
                    <Grid item sm={12} xs={12}>
                      <h1 style={{color:"#2076B8"}}> Info Penghasilan saat Pengajuan</h1>
                    </Grid>

                    <Grid item sm={8} xs={12}>
                      <Grid container spacing={0}>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={6}>
                              Pendapatan perbulan
                            </Grid>
                            <Grid item sm={6} xs={6}>
                              : Rp {formatNumber(this.state.dataBorrower && this.state.dataBorrower.monthly_income, true)}
                            </Grid>
                          </Grid>
                        </Grid>
                      
                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={6}>
                              Penghasilan lain-lain (jika ada)
                            </Grid>
                            <Grid item sm={6} xs={6}>
                              : Rp {formatNumber(this.state.dataBorrower && this.state.dataBorrower.other_income, true)}
                            </Grid>
                          </Grid>
                        </Grid>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={6}>
                              Sumber Penghasilan lain-lain
                            </Grid>
                            <Grid item sm={6} xs={6}>
                              : {this.state.dataBorrower && this.state.dataBorrower.other_incomesource}
                            </Grid>
                          </Grid>
                        </Grid>

                        <Grid item sm={12} xs={12}>
                          <Grid container spacing={0}>
                            <Grid item sm={6} xs={6}>
                              Tanggal Pencairan
                            </Grid>
                            <Grid item sm={6} xs={6}>
                              : {this.state.dataDetail && this.state.dataDetail.disburse_date_changed ? handleFormatDate(this.state.dataDetail && this.state.dataDetail.disburse_date) : handleFormatDate(this.state.dataDetail && this.state.dataDetail.disburse_date)} {this.state.dataDetail && this.state.dataDetail.disburse_date_changed && <b>(Telah Diubah)</b>}
                            </Grid>
                          </Grid>
                        </Grid>

                      </Grid>
                    </Grid>

                    <Grid item sm={4} xs={12}>
                    </Grid>            

                  </Grid>

                  <Grid style={{paddingLeft: '2vh', marginBottom:'2vh'}}>
                      <input type="button" onClick={this.btnCancel}  className="btn btn-primary" value="Kembali"></input>
                  </Grid> 

                </div>
            )
        } else if(!getTokenClient()){
          return (
              <Redirect to='/login' />
          )    
        }
       
    }
}

