import React from 'react';
import {connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import './../../support/css/pagination.css'
import {getPermintaanPinjamanFunction} from './saga'
import Moment from 'react-moment'
import 'moment/locale/id';
import { getTokenClient, getTokenAuth } from '../index/token';
import SearchBar from './../subComponent/SearchBar';
import TableComponent from './../subComponent/TableComponent'
import { checkPermission } from '../global/globalFunction';
const columnDataUser = [
  {
      id: 'id',
      numeric: false,
      label: 'ID Pinjaman',
  },
  {
      id: 'owner_name',
      numeric: false,
      label: 'Nama Nasabah',
  },
  {
      id: 'category',
      numeric: false,
      label: 'Kategori',
  },
    {
      id: 'service',
      numeric: false,
      label: 'Layanan',
    },
    {
    id: 'product',
    numeric: false,
    label: 'Produk',
    },
    {
    id: 'created_time',
    numeric: false,
    label: 'Tanggal Pengajuan',
    },
    {
      id: 'status',
      numeric: false,
      label: 'Status Pinjaman',
    },

]
class PermintaanPinjaman extends React.Component {
  state = {
    rows: [],detailNasabah:{}, searchRows:'',paging:true,
    page: 1,
    rowsPerPage: 10,
    isEdit: false,
    editIndex:Number,
    udahdiklik : false,
    total_data:0,
    last_page:1,
    loading:true,
    errorMessage:''
  };

  //-----------------------------------NIKO FUNCTION-------------------------------------------------------------
  _isMount = false
  componentDidMount(){
    this._isMount=true
    this.getPermintaanPinjamanBtn()
  }
  componentWillUnmount(){
    this._isMount=false
  }
  componentWillReceiveProps(newProps){
    this.setState({errorMessage:newProps.error})
  }

  getPermintaanPinjamanBtn = async function () {
    let params={ 
      status:"processing",
      rows:"1",
      page:this.state.page
    }
    let hasil = this.state.searchRows
    if(hasil){
      //search function
      params.search_all = hasil
    }

    const data = await getPermintaanPinjamanFunction(params)
    const pinjamanList = data.loanRequest && data.loanRequest.data

    for (const key in pinjamanList){
      pinjamanList[key].category = pinjamanList[key].category==="account_executive"?"Account Executive" :pinjamanList[key].category === "agent"?"Agent":"Personal"
      pinjamanList[key].status = pinjamanList[key].status ==="approved"?"Diterima": pinjamanList[key].status==="rejected"?"Ditolak":"Dalam Proses"
      pinjamanList[key].service =  pinjamanList[key].service.toString()
      pinjamanList[key].product =  pinjamanList[key].product.toString()
      pinjamanList[key].created_time =<Moment date={pinjamanList[key].created_time } format=" DD  MMMM  YYYY" />
    }
    if(data){
      console.log(data)
      if(!data.error){
        this.setState({loading:false,rows:data.loanRequest.data,rowsPerPage:data.loanRequest.rows,total_data:data.loanRequest.total_data,last_page:data.loanRequest.last_page,page:data.loanRequest.current_page})
      }else{
        this.setState({errorMessage:data.error})
      }
    }
  }

  onBtnSearch = (e)=>{
      this.setState({loading:true,searchRows:e.target.value,page:1},()=>{
        if(this.state.paging){
          this.getPermintaanPinjamanBtn()
        }
      
      })
  }

  onChangePage = (current) => {
    this.setState({loading:true,page:current},
      ()=>{
        if(this.state.paging){
          this.getPermintaanPinjamanBtn()
        }
      
      })
  }

  render() {
   
  if(getTokenClient() && getTokenAuth()){
      
    return (
        <div className="container">
         <div className="row">
                        <div className="col-6">
                             <h2 className="mt-3">Permintaan Pinjaman - List</h2> {this.state.errorMessage}
                        </div>
                        <div className="col-5 mt-3 ml-5">
                        <div className="input-group">
                        <SearchBar
                             onChange={this.onBtnSearch}
                             placeholder="Search Nama Nasabah, ID Pinjaman.."
                             value={this.state.searchRows}
                          />
                         
                        </div>
                        </div>
          </div>
        <hr></hr>
        < TableComponent
            id={"id"}
            paging={this.state.paging}
            loading={this.state.loading}
            columnData={columnDataUser}
            data={this.state.rows}
            page={this.state.page}
            rowsPerPage={this.state.rowsPerPage}
            totalData={this.state.total_data}
            onChangePage={this.onChangePage}             
            permissionDetail={ checkPermission('lender_loan_request_detail') ? '/permintaanpinjamanDetail/' : null}
        /> 

        
        </div>
      

    );
  
  

}
else if(getTokenAuth()){
  return  <Redirect to='/login' />
}
    
  }
}

const mapStateToProp = (state)=>{
  return{
     
      role: state.user.role,
      id: state.user.id
     
  }
  
}
export default connect (mapStateToProp)(PermintaanPinjaman) ;
